const studentData = $input.first().json;
const lmsData = $input.last().json;

const grades = lmsData.grades || [];
const totalGrade = grades.reduce((sum, grade) => sum + parseFloat(grade.score || 0), 0);
const averageGrade = grades.length > 0 ? (totalGrade / grades.length).toFixed(2) : 'N/A';

const assignments = lmsData.assignments || [];
const completedAssignments = assignments.filter(a => a.status === 'completed').length;
const completionRate = assignments.length > 0 ? ((completedAssignments / assignments.length) * 100).toFixed(1) : '0';

const recentGrades = grades.slice(-3).map(g => parseFloat(g.score || 0));
let trend = 'Stable';
if (recentGrades.length >= 2) {
  const firstHalf = recentGrades.slice(0, Math.floor(recentGrades.length/2));
  const secondHalf = recentGrades.slice(Math.floor(recentGrades.length/2));
  const firstAvg = firstHalf.reduce((a,b) => a+b, 0) / firstHalf.length;
  const secondAvg = secondHalf.reduce((a,b) => a+b, 0) / secondHalf.length;

  if (secondAvg > firstAvg + 5) trend = 'Improving';
  else if (secondAvg < firstAvg - 5) trend = 'Declining';
}

const attendanceRate = lmsData.attendance ? ((lmsData.attendance.present / lmsData.attendance.total) * 100).toFixed(1) : 'N/A';

const subjectsNeedingAttention = grades.filter(grade => parseFloat(grade.score || 0) < 70).map(grade => grade.subject).slice(0, 3);

return [{
  json: {
    student_name: studentData.student_name,
    student_id: studentData.student_id,
    parent_email: studentData.parent_email,
    grade_level: studentData.grade_level,
    report_date: new Date().toISOString().split('T')[0],
    overall_grade: averageGrade,
    assignment_completion_rate: completionRate + '%',
    attendance_rate: attendanceRate + '%',
    performance_trend: trend,
    subjects_needing_attention: subjectsNeedingAttention.join(', ') || 'None',
    total_assignments: assignments.length,
    completed_assignments: completedAssignments,
    recent_grades: recentGrades.join(', '),
    teacher_comments: lmsData.teacher_comments || 'No recent comments'
  }
}];
