const fontColor = "white";
const fontFile = "NotoSerif_Condensed-BlackItalic.ttf";
const fontSize = 80;
const lineHeightMultiplier = 1.1;
const videoWidth = 1080;

const avgCharWidth = fontSize * 0.6;
const maxCharsPerLine = Math.floor(videoWidth / avgCharWidth);

const words = $('GetQuotes').item.json['Video Transcript'].split(' ');
const lines = [];
let currentLine = '';
let currentCharCount = 0;

words.forEach(word => {
  const wordLength = word.length;
  const potentialLength = currentCharCount + (currentLine ? 1 : 0) + wordLength;
  if (potentialLength <= maxCharsPerLine) {
    currentLine += `${currentLine ? ' ' : ''}${word}`;
    currentCharCount = potentialLength;
  } else {
    lines.push(currentLine);
    currentLine = word;
    currentCharCount = wordLength;
  }
});

if (currentLine) lines.push(currentLine);

const totalHeight = lines.length * fontSize * lineHeightMultiplier;
const lineHeight = fontSize * lineHeightMultiplier;

const drawText = lines.map((line, index) => {
  const y = `(h/2)-${Math.ceil(totalHeight/2)}+${index * lineHeight}`;
  return `drawtext=text='${line}':fontsize=${fontSize}:fontcolor=${fontColor}:x=(w-tw)/2:y=${y}:fontfile=${fontFile}`;
}).join(',');

return {
  drawText
};
