// Acc√©der aux headers du webhook via $json.headers
const authHeader = $json.headers?.authorization || '';
const expectedToken = 'Bearer YOUR_TOKEN_HERE YOUR_WEBHOOK_TOKEN_HERE';

// Validation du token
if (!authHeader.startsWith('Bearer ')) {
  return [{ json: {
    status: 'error',
    code: 401,
    message: 'Missing or invalid Authorization header format. Expected: Bearer <token>'
  }}];
}

if (authHeader !== expectedToken) {
  return [{ json: {
    status: 'error',
    code: 401,
    message: 'Invalid authorization token'
  }}];
}

// Acc√©der au body du webhook via $json.body
const body = $json.body;
if (!body || !body.action) {
  return [{ json: {
    status: 'error',
    code: 400,
    message: 'Missing required field: action'
  }}];
}

// Validation des actions support√©es
const validActions = ['audit', 'pause', 'resume', 'duplicate', 'export', 'cleanup'];
if (!validActions.includes(body.action)) {
  return [{ json: {
    status: 'error',
    code: 400,
    message: `Invalid action. Supported: ${validActions.join(', ')}`
  }}];
}

// Si tout est OK
return [{ json: {
  status: 'success',
  action: body.action,
  filters: body.filters || {},
  options: body.options || {},
  originalBody: body
}}];

// Node Code √† ajouter avant Google Sheets
const auditData = $input.all()[0].json;
const processedRows = [];

// Traiter chaque type de rapport (Credentials, Nodes, Instance)
Object.entries(auditData).forEach(([reportKey, reportData]) => {
  if (reportData && reportData.sections) {
    reportData.sections.forEach(section => {
      processedRows.push({
        date: new Date().toISOString(),
        risk_type: reportData.risk,
        report_category: reportKey,
        section_title: section.title,
        description: section.description,
        recommendation: section.recommendation,
        issues_count: section.location ? section.location.length : 0,
        severity: getSeverity(reportData.risk, section.title),
        affected_workflows: section.location ? 
          [...new Set(section.location.map(item => item.workflowId).filter(Boolean))].join('; ') : 'N/A',
        issue_types: section.location ? 
          [...new Set(section.location.map(item => item.kind))].join('; ') : 'N/A'
      });
    });
  }
});

function getSeverity(riskType, sectionTitle) {
  if (riskType === 'nodes' && sectionTitle.includes('risky')) return 'Critical';
  if (riskType === 'instance' && sectionTitle.includes('webhook')) return 'High';
  if (riskType === 'credentials') return 'Medium';
  return 'Low';
}

return processedRows.map(row => ({ json: row }));

// Filtrer les workflows selon les crit√®res sp√©cifi√©s
const workflows = $input.all();
const filters = $('üîÄ Action Router').item.json.filters || {};
const options = $('üîÄ Action Router').item.json.options || {};

// Workflows prot√©g√©s - s√©curit√© renforc√©e
const protectedWorkflows = [
  'n8n-auto-maintenance', // Ne pas pauser le workflow de maintenance !
  'Security',
  'Backup'
];

let filteredWorkflows = workflows;

// Filtrer par nom si sp√©cifi√©
if (filters.namePattern) {
  const regex = new RegExp(filters.namePattern, 'i');
  filteredWorkflows = filteredWorkflows.filter(wf => 
    regex.test(wf.json.name)
  );
}

// Filtrer par tags si sp√©cifi√©
if (filters.tags) {
  const requiredTags = filters.tags.split(',').map(t => t.trim());
  filteredWorkflows = filteredWorkflows.filter(wf =>
    requiredTags.some(tag => 
      wf.json.tags?.some(wfTag => wfTag.name === tag)
    )
  );
