// Filtrer les workflows pour duplication selon les crit√®res optimis√©s avec IA
const workflows = $input.all();
const filters = $('üîÄ Action Router').item.json.filters || {};
const options = $('üîÄ Action Router').item.json.options || {};

// Workflows prot√©g√©s - s√©curit√© renforc√©e
const protectedWorkflows = [
  'n8n-auto-maintenance', // Ne pas dupliquer le workflow de maintenance !
  'Security',
  'Backup'
];

let filteredWorkflows = workflows;

// Filtrer par nom si sp√©cifi√©
if (filters.namePattern) {
  const regex = new RegExp(filters.namePattern, 'i');
  filteredWorkflows = filteredWorkflows.filter(wf => 
    regex.test(wf.json.name)
  );
}

// Filtrer par tags si sp√©cifi√©
if (filters.tags) {
  const requiredTags = filters.tags.split(',').map(t => t.trim());
  filteredWorkflows = filteredWorkflows.filter(wf =>
    requiredTags.some(tag => 
      wf.json.tags?.some(wfTag => wfTag.name === tag)
    )
  );
}

// Exclure les workflows prot√©g√©s (s√©curit√© prioritaire)
filteredWorkflows = filteredWorkflows.filter(wf => {
  if (protectedWorkflows.includes(wf.json.name)) {
    console.warn(`‚ö†Ô∏è Workflow prot√©g√© exclu de la duplication: ${wf.json.name}`);
    return false;
  }
  return true;
});

// Exclure certains workflows critiques
const criticalWorkflows = options.excludeCritical ? 
  ['Security Audit', 'Backup System', 'Health Check'] : [];
filteredWorkflows = filteredWorkflows.filter(wf =>
  !criticalWorkflows.includes(wf.json.name)
);

// üöÄ **LOGIQUE D'INTELLIGENCE ARTIFICIELLE POUR √âVALUER L'INT√âR√äT**

// Fonction utilitaire pour calculer les jours depuis une date
function daysSince(dateString) {
  const date = new Date(dateString);
  const diffTime = Math.abs(new Date() - date);
  return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
}

// Syst√®me de scoring intelligent
filteredWorkflows = filteredWorkflows.filter(wf => {
  const json = wf.json;
  let score = 0;
  let reasons = [];

  // üìä CRIT√àRE 1: Statut d'activit√© (+2 points)
  if (json.active) {
    score += 2;
    reasons.push('actif');
  }

  // üîß CRIT√àRE 2: Complexit√© du workflow (bas√©e sur le nombre de n≈ìuds)
  if (json.nodes && json.nodes.length > 20) {
    score += 3;
    reasons.push(`complexe (${json.nodes.length} n≈ìuds)`);
  } else if (json.nodes && json.nodes.length > 10) {
    score += 1;
    reasons.push(`mod√©r√©ment complexe (${json.nodes.length} n≈ìuds)`);
  }

  // üè∑Ô∏è CRIT√àRE 3: Tags prioritaires (+3 points chacun)
  const priorityTags = ['important', 'critical', 'core', 'production', 'business'];
  if (json.tags) {
    for (const tag of json.tags) {
      if (priorityTags.includes(tag.name.toLowerCase())) {
        score += 3;
        reasons.push(`tag prioritaire: ${tag.name}`);
      }
    }
  }

  // ‚è∞ CRIT√àRE 4: R√©cence des modifications
  if (json.updatedAt) {
    const days = daysSince(json.updatedAt);
    if (days < 7) {
      score += 3;
      reasons.push('r√©cemment modifi√© (<7j)');
    } else if (days < 30) {
      score += 1;
      reasons.push('modifi√© r√©cemment (<30j)');
    }
  }

  // üíº CRIT√àRE 5: Analyse s√©mantique du nom (patterns business-critiques)
  const businessPatterns = [
    /production/i, /business/i, /core/i, /main/i, /primary/i,
    /client/i, /customer/i, /order/i, /payment/i, /integration/i
  ];

  const isBusinessCritical = businessPatterns.some(pattern => 
    pattern.test(json.name)
  );

  if (isBusinessCritical) {
    score += 2;
    reasons.push('nom sugg√©rant un workflow business-critique');
  }

  // üìà CRIT√àRE 6: D√©clencheurs multiples (workflows complexes)
  if (json.triggerCount && json.triggerCount > 1) {
    score += 1;
    reasons.push(`${json.triggerCount} d√©clencheurs`);
  }

  // üéØ D√âCISION FINALE : Seuil d'int√©r√™t
  const isInteresting = score >= 3;

  // Logging pour debug
  if (isInteresting) {
    console.log(`‚úÖ Workflow "${json.name}" s√©lectionn√© (score: ${score}) - Raisons: ${reasons.join(', ')}`);
  } else {
    console.log(`‚ùå Workflow "${json.name}" ignor√© (score: ${score}) - Raisons: ${reasons.join(', ')}`);
  }

  return isInteresting;
});

return filteredWorkflows.map(wf => ({
  json: {
    id: wf.json.id,
    name: wf.json.name,
    active: wf.json.active,
    tags: wf.json.tags,
    action: 'duplicate'
  }
}));

// Pr√©parer les donn√©es pour dupliquer TOUS les workflows
const allWorkflowsData = $('üìã Get Details for Duplicate').all();

console.log(`üîÑ Traitement de ${allWorkflowsData.length} workflows pour duplication`);

const results = [];

allWorkflowsData.forEach((workflowItem, index) => {
  const workflowData = workflowItem.json;

  // G√©n√©rer un nom unique pour chaque copie
  const originalName = workflowData.name;
  const timestamp = new Date().toISOString().split('T')[0]; // Format YYYY-MM-DD
  const duplicateName = `${originalName} - Backup ${timestamp}`;

  // Pr√©parer le payload pour la cr√©ation d'une copie
  const duplicatePayload = {
    name: duplicateName,
    nodes: workflowData.nodes,
    connections: workflowData.connections,
    settings: workflowData.settings || { executionOrder: "v1" },
    active: false, // Cr√©er inactif par s√©curit√©
    staticData: workflowData.staticData || {},
    tags: [
      ...(workflowData.tags || []),
      { 
        name: "backup",
        id: "backup-" + Date.now() + "-" + index
      },
      {
        name: "duplicate",
        id: "duplicate-" + Date.now() + "-" + index
      }
    ]
  };

  // Nettoyer les IDs des n≈ìuds pour √©viter les conflits
  if (duplicatePayload.nodes) {
    duplicatePayload.nodes = duplicatePayload.nodes.map(node => {
      const newNode = { ...node };
      // G√©n√©rer un nouvel ID unique
      newNode.id = 'dup-' + Math.random().toString(36).substr(2, 9) + '-' + Date.now().toString(36) + '-' + index;
      return newNode;
    });
  }

  console.log(`üîÑ Pr√©paration duplication ${index + 1}/${allWorkflowsData.length}: "${originalName}" ‚Üí "${duplicateName}"`);
  console.log(`üìä N≈ìuds √† copier: ${duplicatePayload.nodes?.length || 0}`);

  results.push({ 
    json: { 
      workflowJSON: duplicatePayload,
      originalName: originalName,
      duplicateName: duplicateName,
      originalId: workflowData.id,
      batchIndex: index + 1,
      totalInBatch: allWorkflowsData.length
    }
  });
});

console.log(`‚úÖ ${results.length} workflows pr√©par√©s pour duplication`);
console.log(`üè∑Ô∏è Tags ajout√©s: backup, duplicate (avec index unique)`);

return results;

// Convertir l'objet workflow en JSON string pour l'API n8n
const workflowObj = $json.workflowJSON;

return [{
  json: {
    workflowJSON: JSON.stringify(workflowObj),
    originalName: $json.originalName,
    duplicateName: $json.duplicateName,
    originalId: $json.originalId,
    batchIndex: $json.batchIndex,
    totalInBatch: $json.totalInBatch
  }
}];

// Filtrage intelligent des workflows pour export
const workflows = $input.all();
const filters = $('üîÄ Action Router').item.json.filters || {};
const options = $('üîÄ Action Router').item.json.options || {};

// Workflows syst√®me (toujours inclus en export car critiques)
const systemWorkflows = ['n8n-auto-maintenance', 'Security', 'Backup'];

let filteredWorkflows = workflows;

// üß† INTELLIGENCE ARTIFICIELLE : Syst√®me de scoring pour workflows pertinents
function calculateExportValue(workflow) {
  const json = workflow.json;
  let score = 0;
  let reasons = [];

  // üî• CRIT√àRE 1: Workflows syst√®me (toujours prioritaires)
  if (systemWorkflows.includes(json.name)) {
    score += 10;
    reasons.push('workflow syst√®me critique');
  }

  // üíº CRIT√àRE 2: Workflows business-critiques (analyse s√©mantique)
  const businessPatterns = [
    /production/i, /business/i, /core/i, /main/i, /primary/i,
    /client/i, /customer/i, /order/i, /payment/i, /integration/i,
    /api/i, /webhook/i, /sync/i, /import/i, /export/i, /notification/i
  ];

  const isBusinessCritical = businessPatterns.some(pattern => 
    pattern.test(json.name)
  );

  if (isBusinessCritical) {
    score += 5;
    reasons.push('nom sugg√©rant un workflow business-critique');
  }

  // üè∑Ô∏è CRIT√àRE 3: Tags prioritaires
  const priorityTags = ['important', 'critical', 'core', 'production', 'business', 'live'];
  if (json.tags) {
    for (const tag of json.tags) {
      if (priorityTags.includes(tag.name.toLowerCase())) {
        score += 4;
        reasons.push(`tag prioritaire: ${tag.name}`);
      }
    }
  }

  // ‚ö° CRIT√àRE 4: Workflows actifs (en utilisation)
  if (json.active) {
    score += 3;
    reasons.push('workflow actif');
  }

  // üîß CRIT√àRE 5: Complexit√© technique (workflows sophistiqu√©s)
  if (json.nodes && json.nodes.length > 15) {
    score += 3;
    reasons.push(`workflow complexe (${json.nodes.length} n≈ìuds)`);
  } else if (json.nodes && json.nodes.length > 8) {
    score += 1;
    reasons.push(`workflow mod√©r√©ment complexe (${json.nodes.length} n≈ìuds)`);
  }

  // ‚è∞ CRIT√àRE 6: Activit√© r√©cente (workflows maintenus)
  if (json.updatedAt) {
    const daysSince = Math.ceil((new Date() - new Date(json.updatedAt)) / (1000 * 60 * 60 * 24));
    if (daysSince < 30) {
      score += 2;
      reasons.push('r√©cemment modifi√© (<30j)');
    } else if (daysSince < 90) {
      score += 1;
      reasons.push('modifi√© r√©cemment (<90j)');
    }
  }

  // üìà CRIT√àRE 7: Workflows avec d√©clencheurs (automation active)
  if (json.triggerCount && json.triggerCount > 0) {
    score += 2;
    reasons.push(`${json.triggerCount} d√©clencheur(s)`);
  }

  // üö´ CRIT√àRES DE P√âNALIT√â (workflows √† √©viter)

  // P√©nalit√© pour workflows de test
  const testPatterns = [/test/i, /demo/i, /example/i, /sample/i, /backup.*\d{4}/i];
  const isTestWorkflow = testPatterns.some(pattern => pattern.test(json.name));

  if (isTestWorkflow) {
    score -= 3;
    reasons.push('semble √™tre un workflow de test/demo');
  }

  // P√©nalit√© pour workflows inactifs depuis longtemps
  if (!json.active && json.updatedAt) {
    const daysSince = Math.ceil((new Date() - new Date(json.updatedAt)) / (1000 * 60 * 60 * 24));
    if (daysSince > 180) {
      score -= 2;
      reasons.push('inactif depuis >6 mois');
    }
  }

  return { score, reasons };
}

// Appliquer les filtres manuels d'abord
if (filters.namePattern) {
  const regex = new RegExp(filters.namePattern, 'i');
  filteredWorkflows = filteredWorkflows.filter(wf => 
    regex.test(wf.json.name)
  );
  console.log(`üîç Filtrage par nom: ${filteredWorkflows.length} workflows correspondent √† "${filters.namePattern}"`);
}

if (filters.tags) {
  const requiredTags = filters.tags.split(',').map(t => t.trim());
  filteredWorkflows = filteredWorkflows.filter(wf =>
    requiredTags.some(tag => 
      wf.json.tags?.some(wfTag => wfTag.name === tag)
    )
  );
  console.log(`üè∑Ô∏è Filtrage par tags: ${filteredWorkflows.length} workflows avec tags requis`);
}

// üß† S√âLECTION INTELLIGENTE : Appliquer le scoring
const scoredWorkflows = filteredWorkflows.map(wf => {
  const analysis = calculateExportValue(wf);
  return {
    workflow: wf,
    score: analysis.score,
    reasons: analysis.reasons
  };
});

// Seuil intelligent : workflows avec score >= 5 OU workflows syst√®me
const selectedWorkflows = scoredWorkflows.filter(item => {
  const isSelected = item.score >= 5 || systemWorkflows.includes(item.workflow.json.name);

  if (isSelected) {
    console.log(`‚úÖ "${item.workflow.json.name}" s√©lectionn√© (score: ${item.score}) - ${item.reasons.join(', ')}`);
  } else {
    console.log(`‚ùå "${item.workflow.json.name}" ignor√© (score: ${item.score}) - ${item.reasons.join(', ')}`);
  }

  return isSelected;
});

// Si mode "force include" activ√©, inclure aussi les workflows avec score 3-4
if (options.includeModerateWorkflows) {
  const moderateWorkflows = scoredWorkflows.filter(item => 
    item.score >= 3 && item.score < 5 && !systemWorkflows.includes(item.workflow.json.name)
  );

  moderateWorkflows.forEach(item => {
    selectedWorkflows.push(item);
    console.log(`üü° "${item.workflow.json.name}" inclus (mode √©tendu - score: ${item.score})`);
  });
}

// Statistiques finales
const stats = {
  total: workflows.length,
  afterFilters: filteredWorkflows.length,
  selected: selectedWorkflows.length,
  highValue: selectedWorkflows.filter(item => item.score >= 8).length,
  systemWorkflows: selectedWorkflows.filter(item => 
    systemWorkflows.includes(item.workflow.json.name)
  ).length
};

console.log(`üìä STATISTIQUES EXPORT:`);
console.log(`üì¶ Total workflows: ${stats.total}`);
console.log(`üîç Apr√®s filtres manuels: ${stats.afterFilters}`);
console.log(`‚úÖ S√©lectionn√©s pour export: ${stats.selected}`);
console.log(`üî• Haute valeur (score ‚â•8): ${stats.highValue}`);
console.log(`‚öôÔ∏è Workflows syst√®me: ${stats.systemWorkflows}`);

return selectedWorkflows.map(item => ({
  json: {
    id: item.workflow.json.id,
    name: item.workflow.json.name,
    active: item.workflow.json.active,
    tags: item.workflow.json.tags,
    action: 'export',
    exportScore: item.score,
    exportReasons: item.reasons
  }
}));

// Pr√©parer les donn√©es d'export avec m√©tadonn√©es compl√®tes - VERSION CORRIG√âE
const allWorkflows = $input.all();
const exportTimestamp = new Date().toISOString();
const exportDate = exportTimestamp.split('T')[0]; // Format YYYY-MM-DD

console.log(`üì§ Pr√©paration export de ${allWorkflows.length} workflows s√©lectionn√©s intelligemment`);

// Statistiques en temps r√©el
let stats = {
  totalWorkflows: allWorkflows.length,
  activeWorkflows: 0,
  inactiveWorkflows: 0,
  totalNodes: 0,
  workflowsWithCredentials: 0,
  workflowsWithTriggers: 0,
  systemWorkflows: 0,
  businessCritical: 0,
  highValueWorkflows: 0
};

// Traitement intelligent de chaque workflow
const processedWorkflows = allWorkflows.map((item, index) => {
  const workflow = item.json;
  const nodeCount = workflow.nodes?.length || 0;

  // Mise √† jour des statistiques
  stats.totalNodes += nodeCount;
  if (workflow.active) stats.activeWorkflows++;
  else stats.inactiveWorkflows++;

  // D√©tection des credentials
  const hasCredentials = workflow.nodes?.some(node => 
    node.credentials && Object.keys(node.credentials).length > 0
  ) || false;
  if (hasCredentials) stats.workflowsWithCredentials++;

  // D√©tection des triggers
  const triggerCount = workflow.nodes?.filter(node => 
    node.type.includes('Trigger') || node.type.includes('trigger')
  ).length || 0;
  if (triggerCount > 0) stats.workflowsWithTriggers++;

  // Classification intelligente
  const systemWorkflows = ['n8n-auto-maintenance', 'Security', 'Backup'];
  const isSystemWorkflow = systemWorkflows.includes(workflow.name);
  if (isSystemWorkflow) stats.systemWorkflows++;

  const businessPatterns = [
    /production/i, /business/i, /core/i, /main/i, /primary/i,
    /client/i, /customer/i, /order/i, /payment/i, /integration/i,
    /api/i, /webhook/i, /sync/i, /import/i, /export/i, /notification/i
  ];
  const isBusinessCritical = businessPatterns.some(pattern => 
    pattern.test(workflow.name)
  );
  if (isBusinessCritical) stats.businessCritical++;

  // ‚úÖ CORRECTION : R√©cup√©rer le score depuis les donn√©es du n≈ìud actuel
  const exportScore = item.json.exportScore || 5; // Score par d√©faut si absent
  const exportReasons = item.json.exportReasons || ['s√©lectionn√© par filtrage intelligent'];
  if (exportScore >= 8) stats.highValueWorkflows++;

  // Structure d'export optimis√©e
  return {
    // M√©tadonn√©es principales
    id: workflow.id,
    name: workflow.name,
    active: workflow.active,
    versionId: workflow.versionId,

    // Timestamps
    createdAt: workflow.createdAt,
    updatedAt: workflow.updatedAt,
    exportedAt: exportTimestamp,

    // Structure workflow
    nodes: workflow.nodes,
    connections: workflow.connections,
    settings: workflow.settings || { executionOrder: "v1" },
    staticData: workflow.staticData || {},

    // M√©tadonn√©es organisationnelles
    tags: workflow.tags || [],

    // Analytics et insights pour l'import
    exportMetadata: {
      originallyActive: workflow.active,
      nodeCount: nodeCount,
      triggerCount: triggerCount,
      hasCredentials: hasCredentials,
      isSystemWorkflow: isSystemWorkflow,
      isBusinessCritical: isBusinessCritical,
      exportScore: exportScore,
      exportReasons: exportReasons,

      // Types de n≈ìuds (pour statistiques)
