// Compute delivery duration from "Start Date & Time" (F) and formatted "End" from previous node
// Expects this node to run AFTER "Format End Timestamp" and AFTER the Clients row lookup

const items = $input.all();

// Helper: parse "dd/mm/yyyy at HH:MM"
function parseDT(s) {
  if (!s) return null;
  const m = String(s).match(/(\d{2})\/(\d{2})\/(\d{4}).*?(\d{2}):(\d{2})/);
  if (!m) return null;
  const [, d, M, y, h, min] = m;
  return new Date(Number(y), Number(M) - 1, Number(d), Number(h), Number(min), 0);
}

// Get the formatted end timestamp from the Date & Time node
const endStrFromNode = $node["Format End Timestamp"]?.json?.data || "";

for (const item of items) {
  const row = item.json || {};

  // Try several keys for Start column F (depending on how Google Sheets node labeled it)
  const startStr =
    row["Start Date & Time"] ??
    row["F"] ??
    row["Start"] ??
    "";

  // Prefer the value from Date & Time node; fall back to any end value already present
  const endStr = endStrFromNode || row["End Date & Time"] || "";

  const start = parseDT(startStr);
  const end = parseDT(endStr);

  let duration = "N/A";
  if (start && end) {
    const ms = end - start;
    const days = Math.floor(ms / 86_400_000);
    const hrs = Math.floor((ms % 86_400_000) / 3_600_000);
    const mins = Math.floor((ms % 3_600_000) / 60_000);
    duration = `${days}d ${hrs}h ${mins}m`;
  }

  // Attach results to the item
  item.json.endStr = endStr;
  item.json.duration = duration;
}

return items;

