# The App Script Needed (Use this for your Google Worksheet App Script)

/**
 * ScaleFlow CRM ‚Äî Google Apps Script bridge to n8n
 * Spreadsheet: "ScaleFlow CRM"
 * Tabs: "Leads", "Clients"
 *
 * WHAT IT DOES
 * - When a Lead‚Äôs Stage (Leads!E) changes ‚Üí POST to n8n /lead-stage-changed
 * - When Qualified? checkbox (Leads!H) is edited ‚Üí POST to n8n /lead-qualified
 * - When a Client‚Äôs Project Status (Clients!D) changes ‚Üí POST to n8n /client-status-changed
 *
 * STEPS
 * 1) Paste this into Extensions ‚Üí Apps Script.
 * 2) Replace the WEBHOOK URLs below with your n8n endpoints.
 * 3) Save. First edit will prompt for authorization. (Optional) Run createInstallableTrigger() once.
 */

const WEBHOOKS = {
  STAGE_CHANGED: 'https://{{YOUR_N8N_DOMAIN}}/webhook/lead-stage-changed',
  LEAD_QUALIFIED: 'https://{{YOUR_N8N_DOMAIN}}/webhook/lead-qualified',
  CLIENT_STATUS_CHANGED: 'https://{{YOUR_N8N_DOMAIN}}/webhook/client-status-changed',
};

const TABS = {
  LEADS: 'Leads',
  CLIENTS: 'Clients',
};

// Column indexes (1-based)
const COL = {
  NAME: 1,          // Leads!A
  CLIENT_TYPE: 2,   // Clients!B
  EMAIL: 3,         // Leads/Clients!C
  LEAD_SOURCE: 4,   // Leads!D
  LEAD_STAGE: 5,    // Leads!E
  LEAD_ASSIGNEE: 6, // Leads!F
  PROJECT_STATUS: 4,// Clients!D
  QUALIFIED: 8,     // Leads!H (checkbox)
};

/**
 * MAIN: fires on any cell edit
 */
function onEdit(e) {
  try {
    if (!e || !e.range) return;
    // Only process single-cell edits
    if (e.range.getNumRows() !== 1 || e.range.getNumColumns() !== 1) return;

    const sheet = e.range.getSheet();
    const tabName = sheet.getName();
    const row = e.range.getRow();
    const col = e.range.getColumn();

    // Skip header row
    if (row === 1) return;

    if (tabName === TABS.LEADS) {
      handleLeadsEdit_(sheet, row, col, e);
    } else if (tabName === TABS.CLIENTS) {
      handleClientsEdit_(sheet, row, col, e);
    }
  } catch (err) {
    console.error('onEdit error:', err);
  }
}

/**
 * Handle edits on Leads tab
 */
function handleLeadsEdit_(sheet, row, col, e) {
  // Read row A..H (adjust width if you have more columns)
  const values = sheet.getRange(row, 1, 1, 8).getValues()[0];
  const name = asString_(values[COL.NAME - 1]);
  const email = asString_(values[COL.EMAIL - 1]);
  const sourceText = asString_(values[COL.LEAD_SOURCE - 1]);
  const stageText = asString_(values[COL.LEAD_STAGE - 1]);
  const qualifiedVal = values[COL.QUALIFIED - 1];

  // 1) Stage changed (Leads!E)
  if (col === COL.LEAD_STAGE) {
    const payload = {
      name,
      email,
      source_text: sourceText || '',
      stage: stageText || '',
      previous_stage: e.oldValue || '',
      row: row,
      tab: TABS.LEADS,
    };
    postJson_(WEBHOOKS.STAGE_CHANGED, payload);
  }

  // 2) Qualified? checkbox edited (Leads!H)
  if (col === COL.QUALIFIED) {
    const payload = {
      name,
      email,
      qualified: isChecked_(qualifiedVal),
      row: row,
      tab: TABS.LEADS,
    };
    postJson_(WEBHOOKS.LEAD_QUALIFIED, payload);
  }
}

/**
 * Handle edits on Clients tab
 */
function handleClientsEdit_(sheet, row, col, e) {
  // Read row A..H (adjust width if you have more columns)
  const values = sheet.getRange(row, 1, 1, 8).getValues()[0];
  const email = asString_(values[COL.EMAIL - 1]);
  const projectStatus = asString_(values[COL.PROJECT_STATUS - 1]);

  // 3) Project Status changed (Clients!D)
  if (col === COL.PROJECT_STATUS) {
    const payload = {
      email,
      project_status: projectStatus || '',
      previous_status: e.oldValue || '',
      row: row,
      tab: TABS.CLIENTS,
    };
    postJson_(WEBHOOKS.CLIENT_STATUS_CHANGED, payload);
  }
}

/**
 * POST JSON helper
 */
function postJson_(url, obj) {
  if (!url || url.indexOf('{{YOUR_N8N_DOMAIN}}') !== -1) {
    console.warn('Webhook URL not set. Skipping POST:', url, obj);
    return;
  }
  try {
    const res = UrlFetchApp.fetch(url, {
      method: 'post',
      contentType: 'application/json',
      payload: JSON.stringify(obj),
      muteHttpExceptions: true,
    });
    // Optional: log for debugging
    console.log('POST', url, res.getResponseCode(), safeTrunc_(res.getContentText(), 500));
  } catch (err) {
    console.error('postJson_ error:', url, err && err.message);
  }
}

/**
 * UTILITIES
 */
function isChecked_(val) {
  // Accepts true/false, "TRUE"/"FALSE", 1/0
  if (val === true) return true;
  if (val === false) return false;
  const s = String(val).trim().toUpperCase();
  return s === 'TRUE' || s === '1' || s === 'CHECKED' || s === 'YES';
}

function asString_(v) {
  return v == null ? '' : String(v);
}

function safeTrunc_(s, n) {
  if (!s) return '';
  return s.length > n ? s.slice(0, n) + '‚Ä¶' : s;
}

/**
 * OPTIONAL: create an installable onEdit trigger (runs with full auth scope)
 * Run this once if you want to avoid simple-trigger limitations.
 */
function createInstallableTrigger() {
  const ss = SpreadsheetApp.getActive();
  // Clear existing triggers for safety
  ScriptApp.getProjectTriggers().forEach(t => {
    if (t.getHandlerFunction && t.getHandlerFunction() === 'onEdit') {
      ScriptApp.deleteTrigger(t);
    }
  });
  ScriptApp.newTrigger('onEdit').forSpreadsheet(ss).onEdit().create();
}

/**
 * OPTIONAL: quick test (does not fire onEdit)
 * Replace payloads and run from editor to verify webhooks.
 */
function testPing_() {
  postJson_(WEBHOOKS.STAGE_CHANGED, {
    name: 'Test User',
    email: 'test@example.com',
    source_text: 'Instagram',
    stage: 'Proposal',
    previous_stage: 'Screening',
    row: 2,
    tab: TABS.LEADS,
  });

  postJson_(WEBHOOKS.LEAD_QUALIFIED, {
    name: 'Test User',
    email: 'test@example.com',
    qualified: true,
    row: 2,
    tab: TABS.LEADS,
  });

  postJson_(WEBHOOKS.CLIENT_STATUS_CHANGED, {
    email: 'client@example.com',
    project_status: 'Delivered',
    previous_status: 'In Progress',
    row: 3,
    tab: TABS.CLIENTS,
  });
}
