// Filter for pending quizzes (üü®) and pick the lowest quiz_number
const items = $input.all();
const pending = items.filter(i => i.json.status && i.json.status === 'üü®');
if (pending.length) {
  pending.sort((a,b) => parseInt(a.json.quiz_number) - parseInt(b.json.quiz_number));
  return [pending[0]];
}
return [{ json: { message: 'not exist' } }];
