// Extract .strings files from GitHub tree
const tree = $input.first().json.tree;
const sourceFiles = [];
const targetFiles = [];
const targetLang = $('Config').first().json.TARGET_LANG;

// Find Base.lproj and en.lproj files (source)
tree.forEach(file => {
  if ((file.path.includes('Base.lproj/') || file.path.includes('en.lproj/')) && file.path.endsWith('.strings')) {
    // Extract directory correctly - remove the language folder and filename
    let directory = file.path.replace(/\/(Base|en)\.lproj\/[^\/]+$/, '');
    if (directory === file.path) {
      // If no replacement happened, it's in root
      directory = '';
    }

    sourceFiles.push({
      path: file.path,
      sha: file.sha,
      basename: file.path.split('/').pop(),
      directory: directory
    });
  }
});

// Find target language files (fr.lproj)
tree.forEach(file => {
  if (file.path.includes(`${targetLang}.lproj/`) && file.path.endsWith('.strings')) {
    let directory = file.path.replace(new RegExp(`\\/${targetLang}\\.lproj\\/[^\\/]+$`), '');
    if (directory === file.path) {
      directory = '';
    }

    targetFiles.push({
      path: file.path,
      sha: file.sha,
      basename: file.path.split('/').pop(),
      directory: directory
    });
  }
});

// Create pairs of source and target files
const filePairs = [];
sourceFiles.forEach(sourceFile => {
  const targetFile = targetFiles.find(tf => 
    tf.basename === sourceFile.basename && tf.directory === sourceFile.directory
  );

  // Construct correct target path
  let targetPath;
  if (sourceFile.directory === '') {
    targetPath = `${targetLang}.lproj/${sourceFile.basename}`;
  } else {
    targetPath = `${sourceFile.directory}/${targetLang}.lproj/${sourceFile.basename}`;
  }

  filePairs.push({
    source: sourceFile,
    target: targetFile || null,
    targetPath: targetPath
  });
});

console.log(`Found ${sourceFiles.length} source files, ${targetFiles.length} target files`);
console.log('File pairs:', filePairs);

return filePairs.map(pair => ({ json: pair }));

function parseStringsFile(content) {
  const keys = {};
  if (!content) return keys;

  try {
    const decoded = Buffer.from(content, 'base64').toString('utf-8');
    const lines = decoded.split('\n');

    for (const line of lines) {
      const match = line.match(/^\s*"([^"]+)"\s*=\s*"([^"]*)";?/);
      if (match) {
        keys[match[1]] = match[2];
      }
    }
  } catch (error) {
    console.log('Error parsing content:', error);
  }

  return keys;
}

