// n8n Code node (JavaScript)
// Input:  $input.first().json.results[0].content
// Output: clean plain text (no HTML/JS/CSS, minimal \n)

function stripAll(html) {
  if (typeof html !== 'string') return '';

  // Remove scripts, styles, head, comments, svg, noscript, canvas
  html = html.replace(/<script[\s\S]*?<\/script>/gi, '');
  html = html.replace(/<style[\s\S]*?<\/style>/gi, '');
  html = html.replace(/<head[\s\S]*?<\/head>/gi, '');
  html = html.replace(/<noscript[\s\S]*?<\/noscript>/gi, '');
  html = html.replace(/<svg[\s\S]*?<\/svg>/gi, '');
  html = html.replace(/<canvas[\s\S]*?<\/canvas>/gi, '');
  html = html.replace(/<!--[\s\S]*?-->/g, '');

  // Replace block-level tags with a single newline
  const blockTags = [
    'p','div','section','article','header','footer','nav','aside','main',
    'h1','h2','h3','h4','h5','h6','ul','ol','li','table','tr','td','th','br','hr'
  ];
  for (const tag of blockTags) {
    const rxOpen  = new RegExp(`<${tag}[^>]*>`, 'gi');
    const rxClose = new RegExp(`</${tag}>`, 'gi');
    html = html.replace(rxOpen, '\n');
    html = html.replace(rxClose, '\n');
  }

  // Strip all remaining tags
  let text = html.replace(/<\/?[^>]+>/g, '');

  // Decode common HTML entities
  text = text
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'");

  // Clean whitespace
  text = text
    .replace(/\r/g, '')
    .replace(/[ \t]+/g, ' ')       // collapse spaces/tabs
    .replace(/\n[ \t]+/g,'')    // trim spaces after newlines
    .replace(/\n{3,}/g, '')    // collapse 3+ newlines into 2
    .trim();

  return text;
}

// MAIN
const html = $input.first().json?.results?.[0]?.content || '';
if (!html) {
  return [{ json: { error: 'No HTML found at json.results[0].content' } }];
}

const text = stripAll(html);

return [{
  json: {
    text,
    chars: text.length
  }
}];

// n8n Code node (JavaScript)
// Expected input:
// items[0].json.output = [ { plan_name, monthly_price, annual_price, currency, billing_model, target_team_size, headline_features[], cta_url, notes } ]

function cleanNumber(value) {
  if (value === null || value === undefined || value === '') return null;
  const num = Number(String(value).replace(/[^0-9.]/g, ''));
  return Number.isFinite(num) ? num : null;
}

function mean(nums) {
  const valid = nums.filter(n => Number.isFinite(n));
  return valid.length ? valid.reduce((a, b) => a + b, 0) / valid.length : 0;
}

function currencyFmt(value, currency = 'USD') {
  if (!Number.isFinite(value)) return 'n/a';
  try {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency }).format(value);
  } catch (e) {
    return `$${value.toFixed(2)}`;
  }
}

function safe(value, fallback = 'n/a') {
  if (value === undefined || value === null || value === '') return fallback;
  if (Array.isArray(value) && !value.length) return fallback;
  return value;
}

const allInputs = $input.all();

// Support both: {output:[...]} and direct array input
const plans =
  (allInputs?.[0]?.json?.output && Array.isArray(allInputs[0].json.output))
    ? allInputs[0].json.output
    : (Array.isArray(allInputs?.[0]?.json) ? allInputs[0].json : []);

const normalized = plans.map(plan => ({
  plan_name: safe(plan.plan_name, 'Unnamed Plan'),
  monthly_price: cleanNumber(plan.monthly_price),
  annual_price: cleanNumber(plan.annual_price),
  currency: safe(plan.currency, 'USD'),
  billing_model: safe(plan.billing_model, 'n/a'),
  target_team_size: safe(plan.target_team_size, 'n/a'),
  headline_features: Array.isArray(plan.headline_features) ? plan.headline_features.slice(0, 6) : [],
  cta_url: safe(plan.cta_url, 'n/a'),
  notes: safe(plan.notes, ''),
}));

const currency = normalized.find(p => p.currency)?.currency || 'USD';
const monthlyPrices = normalized.map(p => p.monthly_price).filter(Number.isFinite);
const annualPrices = normalized.map(p => p.annual_price).filter(Number.isFinite);

const planCount = normalized.length;

const avgMonthly = mean(monthlyPrices);
const minMonthly = monthlyPrices.length ? Math.min(...monthlyPrices) : null;
const maxMonthly = monthlyPrices.length ? Math.max(...monthlyPrices) : null;

const avgAnnual = mean(annualPrices);
const minAnnual = annualPrices.length ? Math.min(...annualPrices) : null;
const maxAnnual = annualPrices.length ? Math.max(...annualPrices) : null;

const bestValue = [...normalized]
  .filter(p => Number.isFinite(p.monthly_price))
  .sort((a, b) => a.monthly_price - b.monthly_price)[0];

const premium = [...normalized]
  .filter(p => Number.isFinite(p.monthly_price))
  .sort((a, b) => b.monthly_price - a.monthly_price)[0];

const now = new Date();
const date = now.toLocaleString('en-US', { year: 'numeric', month: 'short', day: '2-digit' });

const tableHeader = '| Plan | Monthly | Annual | Billing Model | Team Fit | Key Features |';
const tableDivider = '| --- | --- | --- | --- | --- | --- |';
const tableRows = normalized.map(plan => {
  const features = plan.headline_features.length ? plan.headline_features.join('<br>') : '‚Äî';
  return `| ${plan.plan_name} | ${currencyFmt(plan.monthly_price, plan.currency)} | ${currencyFmt(plan.annual_price, plan.currency)} | ${plan.billing_model} | ${plan.target_team_size} | ${features} |`;
});

const highlightLines = [];
if (bestValue) {
  highlightLines.push(`**Best self-serve value:** ${bestValue.plan_name} at ${currencyFmt(bestValue.monthly_price, bestValue.currency)} / month.`);
}
if (premium) {
  highlightLines.push(`**Top tier / enterprise:** ${premium.plan_name} peaks at ${currencyFmt(premium.monthly_price, premium.currency)} / month.`);
}
if (planCount >= 4) {
  highlightLines.push('Packaging spans at least 4 tiers, offering clear land-and-expand motion.');
}
if (!highlightLines.length) {
  highlightLines.push('Insufficient pricing variance to highlight standout tiers.');
}

const actionItems = [];
if (maxMonthly && maxMonthly > 0 && minMonthly && minMonthly > 0) {
  const spread = maxMonthly - minMonthly;
  if (spread < 20) {
    actionItems.push('Pricing tiers are tightly grouped ‚Äî consider emphasizing feature differentiation.');
  } else {
    actionItems.push('Pricing spread supports multi-tier upsell narrative; map messaging to each persona.');
  }
}
if (!annualPrices.length) {
  actionItems.push('No annual pricing advertised ‚Äî confirm if annual discounts exist but hidden behind sales.');
}
if (normalized.some(p => !p.headline_features || !p.headline_features.length)) {
  actionItems.push('Add at least three hero features per tier to clarify value proposition.');
}
if (!actionItems.length) {
  actionItems.push('All key pricing signals captured. Proceed to compare vs. internal packaging.');
}

// Safely read config node values (won't throw if node missing)
const cfg = (() => {
  try {
    return $item(0).$node['Configure Target Pricing Page']?.json || {};
  } catch (e) {
    return {};
  }
})();
const sourceUrl = cfg.url || 'Target URL not provided';
const brand = cfg.Brand || 'Target brand';

const markdown =
`# üíº SaaS Pricing Brief
**Brand:** ${brand}

**Source URL:** ${sourceUrl}

**Captured:** ${date}

---

## KPI Snapshot
| Metric | Value |
| --- | --- |
| Plans detected | ${planCount} |
| Avg monthly | ${currencyFmt(avgMonthly, currency)} |
| Range monthly | ${currencyFmt(minMonthly, currency)} ‚Äì ${currencyFmt(maxMonthly, currency)} |
| Avg annual | ${currencyFmt(avgAnnual, currency)} |
| Range annual | ${currencyFmt(minAnnual, currency)} ‚Äì ${currencyFmt(maxAnnual, currency)} |

---

## Tier Comparison
${tableHeader}
${tableDivider}
${tableRows.join('\n')}

---

## Highlights
${highlightLines.map(line => `- ${line}`).join('\n')}

---

## Recommended Next Steps
${actionItems.map(item => `- ${item}`).join('\n')}
`;

const stats = {
  planCount,
  currency,
  monthly: {
    avg: Number(avgMonthly.toFixed(2)),
    min: minMonthly,
    max: maxMonthly,
  },
  annual: {
    avg: Number(avgAnnual.toFixed(2)),
    min: minAnnual,
    max: maxAnnual,
  },
};

return [
  {
    json: {
      markdown,
      stats,
    }
  }
];
