// All people items from the previous node
const peopleItems = $input.all();

// All company items from "Apollo - Get Company Details1"
const companyItems = $('Apollo - Get Company Details').all();

return peopleItems.flatMap((item, idx) => {
  const companyData = companyItems[idx]?.json || {};

  // Prefer organizations[0], else fallback to accounts[0]
  const company = companyData.organizations?.[0] || companyData.accounts?.[0] || {};

  const people = item.json.people || [];

  return people.map(person => ({
    json: {
      company_name: company.name || null,
      company_linkedin_url: company.linkedin_url || null,
      company_twitter_url: company.twitter_url || null,
      company_phone: company.sanitized_phone || company.primary_phone?.sanitized_number || null,
      company_domain: company.primary_domain || company.domain || null,
      person_name: person.name || null,
      person_first_name: person.first_name || null,
      person_last_name: person.last_name || null,
      person_linkedin_url: person.linkedin_url || null,
      person_email: person.email || null,
      person_designation: person.title || null,
      person_city: person.city || null,
      person_country: person.country || null
    }
  }));
});

// Separate base items (with people[]) and email items
const baseItems = items.filter(i => i.json.people);  // companies with people[]
const emailItems = items.filter(i => i.json.person); // email data

// For each company
const updatedCompanies = baseItems.map(base => {
  const company = { ...base.json };

  // For each person in this company, try to match email
  company.people.forEach(person => {
    const match = emailItems.find(e => e.json.person?.id === person.id);
    if (match) {
      person.email = match.json.person?.email || null;
    }
  });

  return { json: company };
});

return updatedCompanies;
