// Count the number of items from the previous node
let numItems = $items().length;

// Check for the specific case where the previous node is empty but returns
// a single item without the expected 'id' key in its JSON.
if (numItems === 1 && $items()[0].json && $items()[0].json.id === undefined) {
  numItems = 0;
}

// Determine the boolean value.
// The new rule is to regenerate when the number is 0, 1, 2, or a multiple of 5.
const regenerate = (numItems === 0 || numItems === 1 || numItems === 2 || numItems % 5 === 0);

// Return a single item with the renamed keys
return [
  {
    json: {
      "Number of snapshots": numItems,
      "Re-generate AI summary?": regenerate
    }
  }
];
