const steps = $input.all().map((item) => item.json);

const result = steps.reduce((acc, step) => {
  const { step: stepIndex, Type, ...rest } = step;
  if (!acc[stepIndex]) {
    acc[stepIndex] = {};
  }
  if (!acc[stepIndex][Type]) {
    acc[stepIndex][Type] = {};
  }
  acc[stepIndex][Type] = rest;
  return acc;
}, {});

return result;

let template = $input.first().json.content
let variables = $input.first().json.variables
let msg = template.replace(/\{\{\s*(\w+)\s*\}\}/g, (match, variableName) => {
    return variables.hasOwnProperty(variableName) 
        ? variables[variableName] 
        : match; // if variable not found, leave as is
});

return {msg}
