// ===== Compact TA for multi-ticker =====
// Input options:
//  A) multiple items: [{ json: { ticker, classCode, timeFrame, bars: [...] } }, ...]
//  B) single item with batch: { json: { batch: [{ticker, classCode, timeFrame, bars:[...]}] } }
//  C) single item with dict:  { json: { barsByTicker: { TICKER: [...], ... }, classCode, timeFrame } }
//
// Output: one item per ticker: { context, last, derived }

const PERIOD_RSI = 14;
const BB_PERIOD = 20, BB_K = 2.0;
const SMA_PERIOD = 20;
const EMA_FAST = 50, EMA_SLOW = 200;
const MACD_FAST = 12, MACD_SLOW = 26, MACD_SIGNAL = 9;
const ADX_PERIOD = 14;

// ---------- helpers ----------
function num(x){ const n = Number(x); return Number.isFinite(n) ? n : NaN; }
function sma(arr, p){
  const out = new Array(arr.length).fill(null); let s=0;
  for(let i=0;i<arr.length;i++){ s+=arr[i]; if(i>=p) s-=arr[i-p]; if(i>=p-1) out[i]=s/p; }
  return out;
}
function ema(arr, p){
  const out=new Array(arr.length).fill(null); const k=2/(p+1);
  let s=0; for(let i=0;i<p;i++) s+=arr[i]; let prev=s/p; out[p-1]=prev;
  for(let i=p;i<arr.length;i++){ prev=arr[i]*k + prev*(1-k); out[i]=prev; }
  return out;
}
function rollingStd(arr,p){
  const out=new Array(arr.length).fill(null);
  for(let i=p-1;i<arr.length;i++){
    const sl=arr.slice(i-p+1,i+1);
    const m=sl.reduce((a,b)=>a+b,0)/sl.length;
    const v=sl.reduce((a,b)=>a+(b-m)*(b-m),0)/sl.length;
    out[i]=Math.sqrt(v);
  }
  return out;
}
function rsi(arr,p=14){
  const out=new Array(arr.length).fill(null); let g=0,l=0;
  for(let i=1;i<=p;i++){ const ch=arr[i]-arr[i-1]; if(ch>0) g+=ch; else l-=ch; }
  let avgG=g/p, avgL=l/p; out[p]=100 - (100/(1 + (avgL===0?Infinity:avgG/avgL)));
  for(let i=p+1;i<arr.length;i++){
    const ch=arr[i]-arr[i-1], gain=Math.max(ch,0), loss=Math.max(-ch,0);
    avgG=(avgG*(p-1)+gain)/p; avgL=(avgL*(p-1)+loss)/p;
    out[i]=100 - (100/(1 + (avgL===0?Infinity:avgG/avgL)));
  }
  return out;
}
function wilder(arr,p){
  const out=new Array(arr.length).fill(null); let s=0;
  for(let i=0;i<p;i++) s+=arr[i]||0; let prev=s/p; out[p-1]=prev; const a=1/p;
  for(let i=p;i<arr.length;i++){ prev=prev + a*((arr[i]||0)-prev); out[i]=prev; }
  return out;
}
function tr(h,l,pc){ return Math.max(h-l, Math.abs(h-pc), Math.abs(l-pc)); }
function adx(highs,lows,closes,p=14){
  const len=closes.length, TR=new Array(len).fill(null), plusDM=new Array(len).fill(null), minusDM=new Array(len).fill(null);
  for(let i=1;i<len;i++){
    const up=highs[i]-highs[i-1], dn=lows[i-1]-lows[i];
    plusDM[i]=(up>dn && up>0)?up:0; minusDM[i]=(dn>up && dn>0)?dn:0; TR[i]=tr(highs[i],lows[i],closes[i-1]);
  }
  const ATR=wilder(TR.map(v=>v??0),p);
  const plusDI=wilder(plusDM.map(v=>v??0),p).map((v,i)=> ATR[i]?100*v/ATR[i]:null);
  const minusDI=wilder(minusDM.map(v=>v??0),p).map((v,i)=> ATR[i]?100*v/ATR[i]:null);
  const DX=plusDI.map((pdi,i)=> (pdi!=null && minusDI[i]!=null && (pdi+minusDI[i])!==0)? 100*Math.abs(pdi-minusDI[i])/(pdi+minusDI[i]) : null);
  const ADX=wilder(DX.map(v=>v??0),p);
  return { plusDI, minusDI, ADX };
}
function clamp01(x){ return Math.max(0, Math.min(1, x)); }

// ---------- core compute for single ticker ----------
function computeOne(meta){
  const raw = Array.isArray(meta.bars) ? meta.bars : [];
  if (raw.length < 30) {
    return {
      error: true,
      payload: { context: { ticker: meta.ticker ?? null }, message: "–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –±–∞—Ä–æ–≤ (–Ω—É–∂–Ω–æ >= 30)" }
    };
  }

  // sort asc & normalize
  const bars = raw.slice().sort((a,b)=> new Date(a.time) - new Date(b.time))
    .map(b=>({
      time:String(b.time),
      open:num(b.open), high:num(b.high), low:num(b.low), close:num(b.close), volume:num(b.volume)
    }));

  const N = bars.length;
  const closes = bars.map(b=>b.close), highs = bars.map(b=>b.high), lows = bars.map(b=>b.low);

  // series
  const rsi14 = rsi(closes, PERIOD_RSI);
  const sma20 = sma(closes, SMA_PERIOD);
  const ema50 = ema(closes, EMA_FAST);
  const ema200 = ema(closes, EMA_SLOW);

  const ema12 = ema(closes, MACD_FAST);
  const ema26 = ema(closes, MACD_SLOW);
  const macdLine = closes.map((_,i)=> (ema12[i]!=null && ema26[i]!=null) ? ema12[i]-ema26[i] : null);
  const macdForSignal = macdLine.map(v=> v==null ? 0 : v);
  const macdSignalCore = ema(macdForSignal.filter((_,i)=> macdLine[i]!=null), MACD_SIGNAL);
  let si=0; const macdSignal = macdLine.map(v=> v!=null ? macdSignalCore[si++] : null);
  const macdHist = macdLine.map((v,i)=> (v!=null && macdSignal[i]!=null) ? v - macdSignal[i] : null);

  const bbMid = sma20;
  const bbStd = rollingStd(closes, BB_PERIOD);
  const bbUpper = bbMid.map((m,i)=> m!=null ? m + BB_K*bbStd[i] : null);
  const bbLower = bbMid.map((m,i)=> m!=null ? m - BB_K*bbStd[i] : null);

  const { plusDI, minusDI, ADX } = adx(highs, lows, closes, ADX_PERIOD);

  // last
  const i = N - 1;
  const price = closes[i];
  const last = {
    price,
    rsi14: rsi14[i],
    macd: macdLine[i],
    macdSignal: macdSignal[i],
    macdHist: macdHist[i],
    bbUpper: bbUpper[i],
    bbMid: bbMid[i],
    bbLower: bbLower[i],
    sma20: sma20[i],
    ema50: ema50[i],
    ema200: ema200[i],
    adx14: ADX[i],
    plusDI: plusDI[i],
    minusDI: minusDI[i],
  };

  const derived = {
    priceAboveSma20: (price!=null && last.sma20!=null) ? (price > last.sma20) : null,
    emaCross: (last.ema50!=null && last.ema200!=null) ? (last.ema50>last.ema200 ? "golden" : last.ema50<last.ema200 ? "death" : "flat") : null,
    macdBias: (last.macd!=null && last.macdSignal!=null) ? (last.macd > last.macdSignal ? "bullish" : "bearish") : null,
    trendStrength: (last.adx14==null) ? null : (last.adx14<20 ? "weak" : last.adx14<40 ? "moderate" : "strong"),
    bbPos01: (last.bbUpper!=null && last.bbLower!=null && price!=null) ? clamp01((price - last.bbLower) / (last.bbUpper - last.bbLower)) : null,
  };

  const context = {
    ticker: meta.ticker ?? null,
    classCode: meta.classCode ?? null,
    timeFrame: meta.timeFrame ?? "H1",
    end: bars[N-1].time
  };

  return { error:false, payload:{ context, last, derived } };
}

// ---------- gather inputs ----------
const inputs = $input.all();   // A) multiple items case

let jobs = [];

// A) multiple items with per-ticker bars
if (inputs.length > 1 || (inputs[0]?.json?.bars && inputs[0]?.json?.ticker)) {
  jobs = inputs.map(it => ({ ticker: it.json.ticker, meta: it.json }));
}
// B) single item with batch: meta.batch = [{ticker, bars, ...}, ...]
else if (Array.isArray(inputs[0]?.json?.batch)) {
  jobs = inputs[0].json.batch.map(x => ({ ticker: x.ticker, meta: x }));
}
// C) single item with dict: meta.barsByTicker = { TICK1:[...], TICK2:[...] }
else if (inputs[0]?.json?.barsByTicker && typeof inputs[0].json.barsByTicker === 'object') {
  const common = { classCode: inputs[0].json.classCode, timeFrame: inputs[0].json.timeFrame };
  jobs = Object.entries(inputs[0].json.barsByTicker).map(([ticker, bars]) => ({
    ticker,
    meta: { ...common, ticker, bars }
  }));
}
// fallback: single standard item
else {
  jobs = [{ ticker: inputs[0]?.json?.ticker ?? null, meta: inputs[0]?.json ?? {} }];
}

// ---------- compute and return one item per ticker ----------
const out = [];
for (const { meta } of jobs) {
  const res = computeOne(meta);
  if (res.error) {
    out.push({ json: { error: res.payload.message || "–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –±–∞—Ä–æ–≤ (–Ω—É–∂–Ω–æ >= 30)", context: { ticker: meta.ticker ?? null } } });
  } else {
    out.push({ json: res.payload });
  }
}

return out;

const data = $input.first()?.json;

const defaultTickers = [ 
  { ticker: "GAZP", classCode: "TQBR" }, 
  { ticker: "SBER", classCode: "TQBR" },
  { ticker: "LKOH", classCode: "TQBR" }
];

// –ï—Å–ª–∏ –µ—Å—Ç—å –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Å ticker –∏ classCode - –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö, –∏–Ω–∞—á–µ –¥–µ—Ñ–æ–ª—Ç–Ω—ã–µ
const result = (data?.ticker && data?.classCode) 
  ? [{ ticker: data.ticker, classCode: data.classCode }]
  : defaultTickers;

return result.map(item => ({ json: item }));
