const inputText = $json["text"];
const lines = inputText.split(/\r?\n/);

let factureIndex = lines.findIndex(line => line.toLowerCase().includes("billed to"));
let name = null;

if (factureIndex >= 0) {
  const lineAfter = lines[factureIndex + 1]?.trim();
  const nextLine = lines[factureIndex + 2]?.trim();

  const isProbablyName = (line) => (
    /[a-z]/.test(line) &&                  // has lowercase
    line.length >= 5 &&                   // not too short
    line !== line.toUpperCase()           // not all uppercase
  );

  if (isProbablyName(lineAfter)) {
    name = lineAfter;
  } else if (isProbablyName(nextLine)) {
    name = nextLine;
  }
}

if (!name) {
  throw new Error("No company name found after 'Billed to'");
}

return [{ company: name }];
