parameters.jsCode:
return items.map(item => {
  if (item.binary && item.binary.data) {
    const bin = item.binary.data;

    // Generate timestamp, e.g. 2025-07-25_153045
    const now = new Date();
    const pad = n => n.toString().padStart(2, '0');
    const timestamp = `${now.getFullYear()}-${pad(now.getMonth()+1)}-${pad(now.getDate())}_${pad(now.getHours())}${pad(now.getMinutes())}${pad(now.getSeconds())}`;

    // Set file extension based on mime type, fallback to .jpg
    const ext = bin.mimeType?.split('/')[1] || 'jpg';

    // Rename file
    bin.fileName = `image-${timestamp}.${ext}`;

    return { json: {}, binary: { data: bin } };
  }
  return item;
});

