const servers = $input.first().json.servers || [];
const documents = [];
const metadata = [];

for (const server of servers) {
  documents.push(`MCP Server: ${server.name}\nDescription: ${server.short_description}`);
  metadata.push(`Name: ${server.name}, Stars: ${server.github_stars}, Downloads: ${server.package_download_count}`);
}

const aiOutputRaw = $('LLM Agent for Decision-Making').first().json.output;
const aiOutput = JSON.parse(aiOutputRaw);

return [{
  json: {
    query: $('User-Query').first().json.chatInput,
    instruction: aiOutput.instruction,   
    documents,
    metadata,
    servers
  }
}];

const results = $input.first().json.results || [];
const servers = $('Parse MCP Server list into documents w metadata').first().json.servers || [];

const top = results.slice(0, 5).map((r, i) => {
  const server = servers[r.index] || {};
  return {
    name: server.name || "Unknown",
    description: server.short_description || "N/A",
    stars: server.github_stars || 0,
    downloads: server.package_download_count || 0,
    score: r.relevance_score
  };
});

let message = "Top MCP Servers \n\n";
top.forEach((s, i) => {
  message += `${i + 1}. ${s.name} (‚≠ê ${s.stars}, ‚¨áÔ∏è ${s.downloads}, üîé ${s.score.toFixed(2)})\n   ${s.description}\n\n`;
});

return [{ json: { message } }];
