// Handle text messages - could be manual transaction entry
const text = $input.first().json.message.text;

// Simple text parsing for manual entries like: "McDonald's $12.50 lunch food"
const parts = text.split(' ');
if (parts.length >= 3) {
  const vendor = parts[0];
  const amountMatch = text.match(/\$?([0-9]+\.?[0-9]*)/); 
  const amount = amountMatch ? parseFloat(amountMatch[1]) : 0;

  return {
    json: {
      ParsedResults: [{
        ParsedText: `Vendor: ${vendor}\nAmount: $${amount}\nDate: ${new Date().toISOString().split('T')[0]}\nType: purchase`
      }]
    }
  };
} else {
  throw new Error('Invalid text format. Use: "Vendor $amount description"');
}

try {
  // Parse AI response
  let aiResponse = $input.first().json.content || $input.first().json.response || $input.first().json;

  // Handle if response is string
  if (typeof aiResponse === 'string') {
    // Try to extract JSON from string response
    const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      aiResponse = jsonMatch[0];
    }
    aiResponse = JSON.parse(aiResponse);
  }

  // Validate required fields
  const requiredFields = ['vendor', 'amount', 'date', 'transaction_type'];
  const missingFields = requiredFields.filter(field => !aiResponse[field]);

  if (missingFields.length > 0) {
    throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
  }

  // Generate UUID alternative
  const generateId = () => {
    return 'txn_' + Math.random().toString(36).substring(2) + Date.now().toString(36);
  };

  // Standardize and clean data
  const transaction = {
    id: generateId(),
    vendor: String(aiResponse.vendor).trim(),
    amount: parseFloat(aiResponse.amount),
    currency: aiResponse.currency || 'USD',
    date: aiResponse.date,
    transaction_type: aiResponse.transaction_type,
    category: aiResponse.category || 'uncategorized',
    processed_date: new Date().toISOString(),
    user_id: $('Telegram Bot Trigger').item.json.message.from.id,
    username: $('Telegram Bot Trigger').item.json.message.from.username || 'unknown'
  };

  // Validate amount is a valid number
  if (isNaN(transaction.amount) || transaction.amount <= 0) {
    throw new Error('Invalid amount detected');
  }

  // Validate date format
  if (!transaction.date.match(/^\d{4}-\d{2}-\d{2}$/)) {
    transaction.date = new Date().toISOString().split('T')[0];
  }

  return { json: transaction };

} catch (error) {
  // Pass error info for error handler
  return {
    json: {
      error: true,
      message: error.message,
      user_id: $('Telegram Bot Trigger').item.json.message.from.id,
      original_data: $input.first().json
    }
  };
}

