// This code finds the latest URL from one of two possible inputs.

// Primary input: A simple text string from a specific n8n node.
const inputText = $('Chat input').first().json.chatInput;

// Fallback input: A conversation history object.
const messages = $input.first().json.messages;

let lastLink = "";

// A regex to find all URLs in a given text.
const urlRegex = /(https?:\/\/[^\s"'<>()]+)/g;

// Function to find the last link in a piece of text.
const findLastLinkInText = (text) => {
  if (typeof text !== 'string') {
    return "";
  }
  const matches = text.match(urlRegex);
  if (matches && matches.length > 0) {
    // Get the last element from the array of matches.
    let link = matches[matches.length - 1];
    // Remove common punctuation from the end of the link.
    return link.replace(/[,.]+$/, '');
  }
  return "";
};

// 1. First, try to find the link in the primary input (inputText).
lastLink = findLastLinkInText(inputText);

// 2. If no link was found in inputText, and messages array exists, search it.
if (!lastLink && messages && Array.isArray(messages) && messages.length > 0) {
  // Iterate backwards through messages to find the most recent human message with a link.
  for (let i = messages.length - 1; i >= 0; i--) {
    const humanMessage = messages[i].human;
    const foundLink = findLastLinkInText(humanMessage);
    if (foundLink) {
      lastLink = foundLink;
      // Once the most recent link is found, stop searching.
      break;
    }
  }
}

// Return the data in the format n8n expects.
// The output will be a JSON object: { "link": "http://found-link.com" }
// or { "link": "" } if no link was found in either source.
return [{
  json: {
    link: lastLink
  }
}];

// n8n Code Node
// This code takes a large HTML string from a property listing, parses it,
// and outputs a compact, formatted text summary. This summary is ideal for
// direct use in an AI assistant's prompt, providing a comprehensive overview
// without extraneous data like links or IDs.

// The input to this node should be a JSON object with a 'data' property containing the HTML string.
// For example: { "data": "<!DOCTYPE html>..." }

// The output will be a single item with a 'summary' property containing the formatted string.
// In case of an error, the 'summary' will state that the link is not supported.

// Import the 'cheerio' library to parse HTML.
const cheerio = require('cheerio');

// The main function that will be executed by the n8n node.
const [input] = items;

// Wrap the entire logic in a try...catch block for robust error handling.
try {
    // Extract the HTML string from the input data.
    const html = input.json.data;

    // If HTML is missing, throw an error to be caught below.
    if (!html) {
        throw new Error("Input data is missing or empty.");
    }

    // Load the HTML into cheerio for parsing.
    const $ = cheerio.load(html);

    // --- Data Extraction ---

    // Extract the main JSON data blob from the '__NEXT_DATA__' script tag.
    const nextDataScript = $('#__NEXT_DATA__').html();
    if (!nextDataScript) {
        throw new Error("Could not find '__NEXT_DATA__' script tag. The HTML structure may have changed.");
    }
    const nextData = JSON.parse(nextDataScript);
    const pageProps = nextData.props.pageProps;
    const propertyData = pageProps.propertyResult.property;
    const insights = pageProps.insights;
    const priceTrends = pageProps.priceTrendsData;
    const similarTransactions = propertyData.similar_price_transactions;


    // Helper function to safely access nested properties
    const get = (p, o) => p.reduce((xs, x) => (xs && xs[x]) ? xs[x] : null, o);

    // --- Text Formatting ---

    // Use template literals to build the formatted text string.
    let summaryText = `**Property Listing Summary**\n\n`;

    // Section: Overview
    summaryText += `**Overview:**\n`;
    // Extract specific property type (e.g., 3M) from the description if available
    const typeMatch = get(['description'], propertyData)?.match(/Type\s*(\w+\d*)/i);
    summaryText += `- **Type:** ${get(['property_type'], propertyData) || 'N/A'}${typeMatch ? ` (${typeMatch[0]})` : ''}\n`;
    const availabilityDate = get(['rental_availability_date'], propertyData);
    summaryText += `- **Status:** For Rent, Available from ${availabilityDate ? new Date(availabilityDate).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }) : 'N/A'}\n\n`;

    // Section: Property Details
    const plotSize = get(['description'], propertyData)?.match(/Plot\s*([\d,]+)\s*sqft/i);
    summaryText += `**Property Details:**\n`;
    const hasStudy = get(['description'], propertyData)?.toLowerCase().includes('study');
    summaryText += `- **Bedrooms:** ${get(['bedrooms'], propertyData) || 'N/A'}${hasStudy ? ' + Study' : ''}\n`;
    summaryText += `- **Bathrooms:** ${get(['bathrooms'], propertyData) || 'N/A'}\n`;
    summaryText += `- **Size (BUA):** ${get(['size', 'value'], propertyData) || 'N/A'} sqft\n`;
    if (plotSize && plotSize[1]) {
        summaryText += `- **Plot Size:** ${plotSize[1].replace(',', '')} sqft\n`;
    }
    summaryText += `- **Furnishing:** ${get(['furnished'], propertyData) || 'N/A'}\n`;
    if (get(['description'], propertyData)?.includes('Fully Upgraded')) {
        summaryText += `- **Condition:** Fully Upgraded with modern finishes\n`;
    }
    summaryText += `\n`;

    // Section: Location & Community
    summaryText += `**Location & Community:**\n`;
    summaryText += `- **Area:** ${get(['location', 'full_name'], propertyData) || 'N/A'}\n`;
    const communityInfo = get(['community'], insights);
    if (communityInfo) {
        summaryText += `- **Community Categories:** ${communityInfo.categories.join(', ')}\n`;
        summaryText += `- **Community Review:** ${communityInfo.review.score.toFixed(1)}/5 from ${communityInfo.review.count} reviews\n`;
    }
    summaryText += `\n`;


    // Section: Pricing & Market Trends
    summaryText += `**Pricing & Market Trends:**\n`;
    summaryText += `- **Rent:** ${get(['price', 'value'], propertyData)?.toLocaleString() || 'N/A'} ${get(['price', 'currency'], propertyData) || ''} per ${get(['price', 'period'], propertyData) || ''}\n`;
    const lastTrend = get(['graph', '1Y'], priceTrends)?.slice(-1)[0];
    if (lastTrend) {
        summaryText += `- **Community Avg. Rent (as of ${lastTrend.period}):** ${lastTrend.communityPrice?.toLocaleString() || 'N/A'} AED/year\n`;
        summaryText += `- **Sub-Community Avg. Rent (as of ${lastTrend.period}):** ${lastTrend.towerPrice?.toLocaleString() || 'N/A'} AED/year\n`;
    }
    summaryText += `\n`;

    // Section: Key Features
    const keyFeaturesMatch = get(['description'], propertyData)?.split('Key Features:')[1];
    if (keyFeaturesMatch) {
        const features = keyFeaturesMatch.split('\n\n')[0]
            .split('\n- ')
            .map(item => item.trim())
            .filter(item => item && !item.toLowerCase().includes('property ref'));
        if (features.length > 0) {
            summaryText += `**Key Features & Amenities:**\n`;
            features.forEach(feature => {
                summaryText += `- ${feature}\n`;
            });
            summaryText += `\n`;
        }
    }

    // Section: Agent & Broker
    summaryText += `**Agent & Broker:**\n`;
    summaryText += `- **Agent:** ${get(['agent', 'name'], propertyData) || 'N/A'}\n`;
    summaryText += `- **Brokerage:** ${get(['broker', 'name'], propertyData) || 'N/A'}\n`;
    summaryText += `- **Agent Experience:** ${get(['agent', 'years_of_experience'], propertyData) || 'N/A'} years\n`;
    summaryText += `- **Agent Closed Deals:** ${get(['agent', 'transactions_count'], propertyData) || 'N/A'}\n`;
    summaryText += `- **Agent Rating:** ${get(['agent', 'avg_score'], propertyData) || 'N/A'}/5 from ${get(['agent', 'review_count'], propertyData) || 'N/A'} ratings\n`;
    const phone = get(['contact_options'], propertyData)?.find(c => c.type === 'phone')?.value;
    const whatsapp = get(['contact_options'], propertyData)?.find(c => c.type === 'whatsapp')?.value;
    const email = get(['contact_options'], propertyData)?.find(c => c.type === 'email')?.value;
    if (phone || whatsapp || email) {
        summaryText += `- **Contact Details:**\n`;
        if (phone) summaryText += `  - Phone: ${phone}\n`;
        if (whatsapp) summaryText += `  - WhatsApp: ${whatsapp}\n`;
        if (email) summaryText += `  - Email: ${email}\n`;
    }


    // Return the formatted text summary on success.
    return [{
        json: {
            summary: summaryText.trim()
        }
    }];

} catch (error) {
    // If any error occurs during the try block (e.g., parsing fails, data structure changes),
    // catch it here and return a standardized error message.
    // This prevents the node from failing and allows the workflow to handle the case gracefully.
    return [{
        json: {
            summary: "The provided link is not supported"
        }
    }];
}
