return items.map(it => {
  const raw = String(it.json.From || "");
  const email = (raw.toLowerCase().match(/<([^>]+)>/)?.[1] || raw.toLowerCase()).trim();
  it.json._fromEmail = email;
  return it;
});

// Use pre-parsed email
const fromEmail = (s) => String(s || '').toLowerCase();

// Build rules from Google Sheet
const rows = $items('Sheet Rules', 0).map(i => i.json);
const rules = rows
  .filter(r => r.Pattern && r.Action)
  .map(r => ({
    pattern: String(r.Pattern).toLowerCase().trim(),
    action: String(r.Action).toUpperCase().trim(),        // DELETE | PROMO | LABEL
    labelName: String(r.LabelName || '').trim(),
    labelId: r.LabelId ? String(r.LabelId).trim() : null,
    removeInbox: String(r.RemoveFromInbox || '').toUpperCase() === 'TRUE',
  }));

return items.map(it => {
  const sender = fromEmail(it.json._fromEmail || it.json.From);
  const hit = rules.find(r => sender.includes(r.pattern));

  it.json.__rule = hit || null;

  // For downstream switches:
  if (!hit) {
    it.json.switchLable = 'extra';
  } else if (hit.action === 'LABEL') {
    it.json.switchLable = hit.labelName || 'extra';
    it.json.labelId = hit.labelId || null; // if provided in sheet
  } else if (hit.action === 'PROMO') {
    it.json.switchLable = 'PROMO';
  } else if (hit.action === 'DELETE') {
    it.json.switchLable = 'DELETE';
  }

  return it;
});

// Build name ‚Üí id map from Gmail Labels node
const labels = $items('Gmail Labels', 0).map(i => i.json);
const map = Object.fromEntries(
  labels.filter(l => l && l.name && l.id)
        .map(l => [String(l.name).trim(), l.id])
);

return items.map(item => {
  const key = String(item.json.switchLable || '').trim();

  // Only look up when we don't already have an id and key is a real label name
  if (!item.json.labelId && key && key !== 'PROMO' && key !== 'DELETE' && key !== 'extra') {
    item.json.labelId = map[key] || null;
  }
  return item;
});
