/* Build a single prompt for the LLM to produce a Slack-ready brief.
 * No tool calls‚Äîjust return concise Markdown text.
 */
const tasks = $input.all().map(i => i.json);

const compact = tasks.map(t => ({
  title: t.title || '(untitled)',
  notes: t.notes || '',
  due: t.due || t.updated || '',
  webViewLink: t.webViewLink || '',
  links: (t.links || []).map(l => `${l.type || 'link'}: ${l.link || ''}`),
  parent: t.parent || null,
}));

const instructions = `You are an executive assistant. Create a concise Slack-ready morning brief for Google Tasks due TODAY.
Requirements:
- Start with a one-sentence overview (calm, actionable).
- Then bullets: **Title** ‚Äî 1-sentence goal; 1‚Äì2 next steps; default owner "Me"; ETA (morning/afternoon/evening).
- If notes contain a URL or webViewLink exists, show one key link inline.
- Group obvious subtasks under a parent with indentation.
- Keep it under ~120 words. No fluff.
- Output plain Markdown suitable for Slack.`;

const context = `DATA (JSON):\n${JSON.stringify(compact, null, 2)}`;

return [{ json: { input: `${instructions}\n\n${context}` } }];

// Remove <think>...</think> blocks from the LLM output
let text = $json.text || "";

text = text.replace(/<think>[\s\S]*?<\/think>/gi, "").trim();

return [{ json: { cleanText: text } }];

/* Filter Google Tasks that are due TODAY in Asia/Dhaka.
 * Emits N items (one per task) when there are tasks due today.
 * Emits a single item { __empty: true } when none.
 */
const TZ = 'Asia/Dhaka';
const toTZ = (d) => new Date(d.toLocaleString('en-US', { timeZone: TZ }));
const now = new Date();
const start = toTZ(new Date(now.getFullYear(), now.getMonth(), now.getDate()));
const end = toTZ(new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1));
function isDueToday(dueRaw) {
  if (!dueRaw) return false;
  const iso = dueRaw.length === 10 ? `${dueRaw}T00:00:00Z` : dueRaw;
  const d = toTZ(new Date(iso));
  return d >= start && d < end;
}
const dueToday = [];
for (const item of $input.all()) {
  const t = item.json || {};
  const status = (t.status || '').toLowerCase();
  if (status === 'completed' || status === 'hidden' || status === 'deleted') continue;
  if (isDueToday(t.due || '')) {
    dueToday.push({ json: t });
  }
}
if (dueToday.length === 0) {
  return [{ json: { __empty: "empty" } }];
}
return dueToday;
