// n8n Code node (JavaScript)

// Helper: decode Gmail's base64url
function decodeBase64Url(str) {
  if (!str) return "";
  str = str.replace(/-/g, '+').replace(/_/g, '/');
  return Buffer.from(str, 'base64').toString('utf8');
}

// Helper: clean text
function cleanText(txt) {
  if (!txt) return "";

  return txt
    // remove long URLs
    .replace(/https?:\/\/\S+/g, "")
    // remove multiple stars/dashes
    .replace(/[*]{3,}|[-]{3,}/g, "")
    // remove extra blank lines
    .replace(/\n\s*\n\s*\n+/g, "\n\n")
    // trim
    .trim();
}

const items = $input.all();
const returnData = [];

for (let i = 0; i < items.length; i++) {
  const email = items[i].json;
  let plainText = "";

  // Case 1: direct body
  if (email?.payload?.body?.data) {
    plainText = decodeBase64Url(email.payload.body.data);
  }

  // Case 2: multipart body
  if (!plainText && Array.isArray(email?.payload?.parts)) {
    for (const part of email.payload.parts) {
      if (part.mimeType === "text/plain" && part.body?.data) {
        plainText = decodeBase64Url(part.body.data);
        break;
      }
    }
  }

  // Fallback if you already had email.text
  if (!plainText && email.text) {
    plainText = email.text;
  }

  const extractedData = {
    id: email.id || "",
    subject: email.subject || email.payload?.headers?.find(h => h.name === "Subject")?.value || "",
    from: email.from?.text || email.from || email.payload?.headers?.find(h => h.name === "From")?.value || "",
    to: email.to?.text || email.to || email.payload?.headers?.find(h => h.name === "To")?.value || "",
    plainText: cleanText(plainText),
  };

  returnData.push({ json: extractedData });
}

return returnData;

const output = $json["output"]["text"];

// Match plain text - works for both "PLAIN TEXT" and "PLAIN TEXT DIGEST"
const plainText = output.match(/PLAIN TEXT(?: DIGEST)?:\s*([\s\S]*?)(?:\n\n---\n\nMARKDOWN DIGEST:|\n\nMARKDOWN:)/)?.[1]?.trim();

// Match markdown - works for both "MARKDOWN" and "MARKDOWN DIGEST"
const markdown = output.match(/MARKDOWN(?: DIGEST)?:\s*([\s\S]*)/)?.[1]?.trim();

return [{
  plainText,
  markdown
}];
