let text = $json.output;

// 1. Zamie≈Ñ **co≈õ** na <b>co≈õ</b>
text = text.replace(/\*\*(.*?)\*\*/g, '<b>$1</b>');

// 2. Zamie≈Ñ *   <b>Co≈õ:</b> ... na bullet z boldem
text = text.replace(/^\*\s+<b>([^<]+):<\/b>/gm, '‚Ä¢ <b>$1:</b>');

// 3. Zamie≈Ñ *   Co≈õ: ... na bullet z boldem (gdy nie ma bolda)
text = text.replace(/^\*\s+([^:]+):/gm, '‚Ä¢ <b>$1:</b>');

// 4. Zamie≈Ñ `code` na <code>
text = text.replace(/`([^`]+)`/g, '<code>$1</code>');

// 5. Zamie≈Ñ podw√≥jne nowe linie na pojedyncze (≈ºeby nie by≈Ço za du≈ºych przerw)
text = text.replace(/\n{3,}/g, '\n\n');

// 6. (Opcjonalnie) Zamie≈Ñ linki [tekst](url) na <a href="url">tekst</a>
text = text.replace(/\[([^\]]+)\]\(([^)]+)\)/g, '<a href="$2">$1</a>');

// 7. Chunking
const chunkSize = 4096;
const chunks = [];
for (let i = 0; i < text.length; i += chunkSize) {
  chunks.push({ text: text.substring(i, i + chunkSize) });
}

return chunks;

// Helper: bezpieczne czytanie z innych nod√≥w
function safe(getter, fallback = undefined) {
  try {
    const v = getter();
    return v === undefined || v === null ? fallback : v;
  } catch (e) {
    return fallback;
  }
}

// Spr√≥buj pobraƒá oba ≈∫r√≥d≈Ça, ale bez crasha:
const tgMsg   = safe(() => $('Telegram Trigger').item.json.message, null);
const chatEvt = safe(() => $('When chat message received').item.json, null);

// Pick-first
const pick = (...arr) => arr.find(v => typeof v === 'string' && v.trim()) || '';

// Tekst z r√≥≈ºnych miejsc (STT -> chatInput, tekst, itd.)
const inputText = pick(
  $json.inputText,
  $json.chatInput,
  $json.text,
  $json.message?.text,
  tgMsg?.text,
  chatEvt?.message,
  chatEvt?.input,
  chatEvt?.text
);

// ≈πr√≥d≈Ço
let source = 'chat';
if (tgMsg) source = 'telegram';
else if (chatEvt) source = 'n8n-chat';

// SessionId
let sessionId = 'unknown';
if (tgMsg) {
  sessionId = String(tgMsg.chat?.id ?? tgMsg.from?.id ?? 'telegram-unknown');
} else if (chatEvt) {
  sessionId = String(
    chatEvt.sessionId ?? chatEvt.chatId ?? chatEvt.connectionId ?? chatEvt.userId ?? 'n8n-chat-unknown'
  );
} else if ($json.chatId) {
  sessionId = String($json.chatId);
}

// Voice flag (na przysz≈Ço≈õƒá)
const isVoice = Boolean(safe(() => $('Telegram Trigger').item.json.message.voice.file_id, null));

return [{
  json: {
    inputText: inputText.trim(),
    source,
    sessionId,
    isVoice
  }
}];
