  for (const [key, value] of Object.entries(item.binary)) {
    results.push({
      json: {
        fileKey: key,
        fileName: value.fileName,
        mimeType: value.mimeType,
        fileExtension: value.fileExtension,
        fileSize: value.fileSize
      },
      binary: {
