// --- SAFER INPUT HANDLING ---
const text = ($json.text || "").toLowerCase();
const numpages = $json.numpages || 1;

let score = 0;
let reasons = []; 

// --- KEYWORD LISTS ---
const nlSpaces = ["woonkamer", "keuken", "slaapkamer", "badkamer", "overloop", "entree", "hal", "toilet", "berging", "techniek", "meterkast", "mk"];
const enSpaces = ["living room", "kitchen", "bedroom", "bathroom", "landing", "entrance", "hall", "toilet", "storage", "utility room"];
const floorWords = ["verdieping", "begane grond", "zolder", "kelder", "vliering", "doorsnede", "gevel", "plattegrond"];
const enFloorWords = ["floor", "ground floor", "attic", "basement", "cross-section", "facade", "floor plan"];
const symbols = ["wcd", "th", "wp", "v.v.", "wm", "wd", "wtw", "pv", "rad.", "rm", "gr"];
const projectWords = ["bouwnummer", "opdrachtgever", "project", "schaal", "datum", "bladnummer", "formaat"];

// --- CHECKS ---
// 1. Spaces
const foundNlSpaces = nlSpaces.filter(w => text.includes(w));
const foundEnSpaces = enSpaces.filter(w => text.includes(w));
if ((foundNlSpaces.length + foundEnSpaces.length) >= 3) {
  score += 0.35;
  reasons.push(`Found ${foundNlSpaces.length + foundEnSpaces.length} room names`);
}

// 2. m¬≤ matches
const m2Matches = text.match(/\d+(?:[.,]\d+)?\s*m(?:¬≤|2)/g) || [];
if (m2Matches.length >= 3) {
  score += 0.25;
  reasons.push(`Found ${m2Matches.length} m¬≤ measurements`);
}

// 3. mm matches (dimensions)
const mmMatches = text.match(/\d{3,}\s*mm/g) || [];
if (mmMatches.length >= 3) {
  score += 0.15;
  reasons.push(`Found ${mmMatches.length} mm measurements`);
}

// 4. Symbols
const foundSymbols = symbols.filter(s => text.includes(s.toLowerCase()));
if (foundSymbols.length >= 3) {
  score += 0.15;
  reasons.push(`Found ${foundSymbols.length} technical symbols`);
}

// 5. Floor/plan words
const foundFloors = [...floorWords, ...enFloorWords].filter(w => text.includes(w));
if (foundFloors.length >= 2) {
  score += 0.10;
  reasons.push(`Found ${foundFloors.length} floor/drawing parts`);
}

// 6. Project words
const foundProjectWords = projectWords.filter(w => text.includes(w));
if (foundProjectWords.length >= 3) {
  score += 0.10;
  reasons.push(`Found ${foundProjectWords.length} project info words`);
}

// --- NORMALIZE SCORE ---
if (score > 1) score = 1;

// ‚úÖ Return one item with JSON + preserve binary
return {
  json: {
    ...$json,
    confidence: parseFloat(score.toFixed(2)),
    analysis: {
      reasons,
      foundNlSpaces,
      foundEnSpaces,
      m2Matches,
      mmMatches,
      foundSymbols,
      foundFloors,
      foundProjectWords,
      pages: numpages
    }
  },
   binary: item.binary // pass binary unchanged
};

