// Assuming the JSON data is stored in a variable called "jsonData"
const product = $("get Product without description").first().json.items[0];
const data = $('Get attributes and options').first().json.items; // Extract the items containing attributes and options
const customAttributes = product.custom_attributes; // Extract the custom attributes
//console.log(customAttributes);
// Create a map of attribute_code to their options (for select-type attributes)
const attributeOptionsMap = {};
for (const attribute of data) {
    if (attribute.options) {
        attributeOptionsMap[attribute.attribute_code] = attribute.options;
    }
}
// Loop over all custom attributes to check if they exist in the attributeOptionsMap
for (const customAttribute of customAttributes) {
    const attributeCode = customAttribute.attribute_code;
            //console.log(attributeCode);

    // Check if this custom attribute exists in the attributeOptionsMap (i.e., it has options)
    if (attributeOptionsMap[attributeCode]) {

        const attributeOptions = attributeOptionsMap[attributeCode];
        const value = customAttribute.value;
            //console.log(value);

        // Find the label corresponding to the current value
        const matchingOption = attributeOptions.find(option => option.value === value);
        if (matchingOption) {
            // Replace the value with the label
            customAttribute.value = matchingOption.label;
        }
    }
}
// After this, customAttributes will have all applicable values replaced by their labels
product.custom_attributes = customAttributes;
return product;
