parameters.jsonBody:
={
  "requests": [
    {
      "replaceAllText": {
        "containsText": {
          "text": "$client",
          "matchCase": true
        },
        "replaceText": "{{ $('Extract call info').item.json.client }}"
      }
    },
    {
      "replaceAllText": {
        "containsText": {
          "text": "$date",
          "matchCase": true
        },
        "replaceText": "{{ new Date().toLocaleDateString('fr-FR') }}"
      }
    },
    {
      "replaceAllText": {
        "containsText": {
          "text": "$ambition",
          "matchCase": true
        },
        "replaceText": "{{ $('Generate slide content').item.json.message.content.ambition }}"
      }
    },
    {
      "replaceAllText": {
        "containsText": {
          "text": "$challenges",
          "matchCase": true
        },
        "replaceText": "{{ $('Format challenges').item.json.challengesFormatted }}"
      }
    },
    {
      "createImage": {
        "url": "https://logo.clearbit.com/{{ $('Extract call info').item.json.domain }}",
        "elementProperties": {
          "pageObjectId": "g33acd02fb35_0_0",
          "size": {
            "height": {
              "magnitude": 50,
              "unit": "PT"
            },
            "width": {
              "magnitude": 100,
              "unit": "PT"
            }
          },
          "transform": {
            "scaleX": 1,
            "scaleY": 1,
            "translateX": 0,
            "translateY": 330,
            "unit": "PT"
          }
        }
      }
    }
  ]
}
