// Function to process ALL input items (multiple products)
const allItems = $input.all();
const processedItems = [];

// Function to process a single product
function processProductData(inputData) {
  try {
    const result = {
      product_id: null,
      title: null,
      handle: null,
      description: '',        // keep original HTML
      cleanDescription: '',   // plain text
      imageSources: [],       // Always an array
      totalImages: 0,
      error: null
    };

    // Add fields as is
    if (inputData.product_id !== undefined && inputData.product_id !== null) {
      result.product_id = inputData.product_id;
    }
    if (inputData.title !== undefined && inputData.title !== null) {
      result.title = inputData.title;
    }
    if (inputData.handle !== undefined && inputData.handle !== null) {
      result.handle = inputData.handle;
    }

    // Process description
    if (inputData.description) {
      // 1. Keep original HTML (Shopify supports this)
      result.description = inputData.description;

      // 2. Generate plain text version
      let cleanDescription = inputData.description
        .replace(/<[^>]*>/g, '') // strip tags
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/&#39;/g, "'")
        .replace(/&nbsp;/g, ' ')
        .replace(/\n+/g, '\n')
        .replace(/[ \t]+/g, ' ')
        .replace(/\n /g, '\n')
        .trim();

      // escape for JSON safety
      cleanDescription = cleanDescription
        .replace(/\\/g, '\\\\')
        .replace(/"/g, '\\"')
        .replace(/\n/g, '\\n');

      result.cleanDescription = cleanDescription;
    }

    // Process images - ENSURE IT'S AN ARRAY
    if (inputData.Images && Array.isArray(inputData.Images)) {
      result.imageSources = inputData.Images
        .filter(image => image && image.src && image.src.trim() !== '')
        .map(image => image.src.trim())
        .filter((src, index, array) => array.indexOf(src) === index);

      result.totalImages = result.imageSources.length;
    }
    // Handle case where Images might be a string
    else if (inputData.Images && typeof inputData.Images === 'string') {
      try {
        const parsedImages = JSON.parse(inputData.Images);
        if (Array.isArray(parsedImages)) {
          result.imageSources = parsedImages
            .filter(image => image && image.src && image.src.trim() !== '')
            .map(image => image.src.trim())
            .filter((src, index, array) => array.indexOf(src) === index);

          result.totalImages = result.imageSources.length;
        }
      } catch (parseError) {
        console.log('Error parsing Images string:', parseError);
        result.imageSources = [];
        result.totalImages = 0;
      }
    }

    return result;

  } catch (error) {
    return {
      product_id: inputData.product_id || null,
      title: inputData.title || null,
      handle: inputData.handle || null,
      description: '',
      cleanDescription: '',
      imageSources: [],
      totalImages: 0,
      error: error.message
    };
  }
}

// Process each item
for (let i = 0; i < allItems.length; i++) {
  const processedItem = processProductData(allItems[i].json);
  processedItems.push({ json: processedItem });
}

// Return all processed items
return processedItems;

// Get input JSON
let input = $input.first().json;

// Function to convert raw HTML into Shopify-safe JSON string
function makeJsonSafe(html) {
  return html
    .replace(/\\/g, "\\\\")    // escape backslashes
    .replace(/"/g, '\\"')      // escape quotes
    .replace(/\n/g, "\\n")     // escape line breaks
    .replace(/\r/g, "");       // remove carriage returns (Windows line breaks)
}

// Process body_html
let safeContent = makeJsonSafe(input.content);

// Return all fields in correct JSON structure
return {
  json: {
    blog_title: input.blog_title,
    excerpt: input.excerpt,
    page_title: input.page_title,
    meta_description: input.meta_description,
    "URL _handle": input["URL _handle"],
    blog_status: input.blog_status,
    body_html: safeContent
  }
};
