function adjustDateForWeekend(original_date){
  const new_date = new Date(original_date)
  const day = new_date.getDay();
  // check for Saturday: add 2 days if necessary
  if(day == 6){
    new_date.setDate(new_date.getDate() + 2)
  }
  // check for Sunday: add 1 day if necessary
  if(day == 0){
    new_date.setDate(new_date.getDate() + 1)
  }
  //return the new day in format YYYY-mm-dd
  return new_date.toISOString().slice(0,10)
}

// Loop over all the deadlines and adjust to the next monday if the original date is during the weekend
for (const item of $input.all()) {
  item.json.Deadline = adjustDateForWeekend(item.json.Deadline);
}

return $input.all();
