// Read raw text from previous node
const data = items[0].json;
const rawText = String(
  data.output ||
  data.outputText ||
  data.Output ||
  data.summary ||
  data.result ||
  ""
);

if (!rawText) {
  return [{
    json: {
      error: "No input text found in previous node (tried output / outputText / Output / summary / result)."
    }
  }];
}

// Helper: extract section
function extractSection(text, sectionName) {
  if (!text) return "";
  const nameEsc = sectionName.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");

  // 1) Bold markdown header: **Section Name**
  let regex = new RegExp(`\\*\\*\\s*${nameEsc}\\s*\\*\\*[\\r\\n]+([\\s\\S]*?)(?=\\n\\*\\*|\\n---|$)`, "i");
  let m = text.match(regex);
  if (m) return m[1].trim();

  // 2) Plain header line
  regex = new RegExp(`^\\s*${nameEsc}\\s*$[\\r\\n]+([\\s\\S]*?)(?=^\\s*\\*\\*|\\n---|$)`, "im");
  m = text.match(regex);
  if (m) return m[1].trim();

  // 3) Fallback: find the name anywhere
  regex = new RegExp(nameEsc, "i");
  m = text.match(regex);
  if (m) {
    const start = m.index + m[0].length;
    const rest = text.slice(start);
    const nextBoundary = rest.search(/\n\*\*|\n---/i);
    const end = nextBoundary !== -1 ? start + nextBoundary : text.length;
    return text.slice(start, end).trim();
  }

  return "";
}

// Extract score + justification
function extractScoreAndJustification(block) {
  if (!block) return ["", ""];
  const sanitized = block.replace(/\*/g, "").trim();

  let score = "";
  let justification = "";

  const scoreMatch = sanitized.match(/Score\s*[:\-â€“â€”]?\s*([0-9]{1,2}(?:\/10)?|N\/A|NA|n\/a)/i);
  if (scoreMatch) {
    score = scoreMatch[1].trim();
    if (/^[0-9]{1,2}$/.test(score)) {
      const n = parseInt(score, 10);
      if (n >= 0 && n <= 10) score = `${n}/10`;
    }
  }

  const justMatch = sanitized.match(/Justification\s*[:\-â€“â€”]?\s*([\s\S]*)/i);
  if (justMatch) {
    justification = justMatch[1].trim();
  }

  if (!score && sanitized) score = "N/A";
  return [score, justification];
}

// Extract sections
const educationalQualification = extractSection(rawText, "Educational Qualifications");
const jobHistory = extractSection(rawText, "Job History");
const skillSet = extractSection(rawText, "Skill Set");
const candidateEvaluation = extractSection(rawText, "Candidate Evaluation");

// Get score + justification
const [score, justification] = extractScoreAndJustification(candidateEvaluation);

return [{
  json: {
    educationalQualification: educationalQualification || "",
    jobHistory: jobHistory || "",
    skillSet: skillSet || "",
    score: score || "",
    justification: justification || ""
  }
}];
