// Get items from previous nodes
const items = $input.all();

// Create a message for Telegram
let message = "üìä *Lead Enrichment Results:*\n\n";

// Add Google Sheet information using the provided syntax
const sheetName = $('Google Drive Trigger1').first().json.name;
const sheetUrl = $('Google Drive Trigger1').first().json.webViewLink;

// Add sheet name and URL to message
message += `*Sheet Name:* ${sheetName}\n`;
message += `*URL:* ${sheetUrl}\n\n`;

// Process all items
items.forEach((item, index) => {

  // Check if item has json data
  if (item.json) {
    // Add score and status if available
    if (item.json.score) message += `*Score:* ${item.json.score}\n`;
    if (item.json.status) message += `*Status:* ${item.json.status}\n`;
  }

  message += "\n";
});

// Log for debugging
console.log("Message prepared:", message);

// Return the message to be sent to Telegram
return [{ json: { message } }];

