return items.map(item => {
  const html = item.json.html || "";

  const text = html
    .replace(/<\s*br\s*\/?>/gi, "\n")
    .replace(/<\/p\s*>/gi, "\n")
    .replace(/<[^>]*>/g, "")
    .replace(/&nbsp;/g, " ")
    .replace(/&amp;/g, "&")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">")
    .replace(/&quot;/g, "\"")
    .replace(/&#39;/g, "'")
    .replace(/(\r\n|\n|\r|\\n|\\r|\\\\n)/g, "")  
    .replace(/\s+/g, " ")
    .replace(/!\S+?\.(png|jpe?g|gif|bmp|pdf|docx?|xlsx?|zip|rar)(\|[^!]*)?!/gi, "")
    .replace(/\{panel:[^}]*\}/gi, "")

    .trim();

  return {
    json: {
      ...item.json,
      text
    }
  };
});

const issueMap = {};

items.forEach(item => {
  const comments = item.json.comments || [];

  comments.forEach(comment => {
    const url = comment.self || '';
    const match = url.match(/issue\/(\d+)\//);
    const issueId = match ? match[1] : 'unknown';

    const body = comment.body?.trim();
    if (!body) return;

    // Filtri per commenti inutili
    const onlyImage = /^!?[^\s]+\.(png|jpg|jpeg|gif|bmp)\|?.*!?$/i.test(body);
    const onlyDot = body === '.';
    const isEmptyMarkdown = /^(\*|_|~|`)+$/g.test(body);
    if (onlyImage || onlyDot || isEmptyMarkdown) return;

    if (!issueMap[issueId]) {
      issueMap[issueId] = {
        issue_id: issueId,
        comments: []
      };
    }

    issueMap[issueId].comments.push(body);
  });
});

// Output finale
return Object.values(issueMap).map(issue => ({
  json: {
    issue_id: issue.issue_id,
    comments_text: issue.comments.join(';')  
  }
}));

return items[0].json.issues.map(issue => {
  const rawDate = issue.fields.customfield_10317?.ongoingCycle?.startTime?.jira ?? null;
  const formattedDate = rawDate 
    ? new Date(rawDate).toLocaleString("it-IT", {
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        hour12: false
      }).replace(',', '') 
    : null;

  return {
    json: {
      issue_key: issue.key ?? null,
      issue_id: issue.id ?? null,
      summary: issue.fields.summary ?? null,
      issue_description: issue.fields?.description ?? null,
      product: issue.fields.customfield_10402?.[0].replace(/\s*\(.*?\)\s*/g, '').trim() ?? null,
      level: issue.fields.customfield_10408?.[0].replace(/\s*\(.*?\)\s*/g, '').trim() ?? null,
      customer: issue.fields.customfield_10409?.[0]?.replace(/\s*\(.*?\)\s*/g, '').trim() ?? null,
      status: issue.fields.customfield_10001?.currentStatus?.status ?? null,
      classification: issue.fields.customfield_10322?.value ?? null,
      registration: formattedDate
    }
  };
});

