(() => {
  // Prefer aggregated array, else fall back to current items
  const arr = Array.isArray($json.articles) ? $json.articles
            : ($items && $items.length ? $items.map(i => i.json) : []);
  if (!arr.length) return "No fresh items today.";
  // Escape for Slack mrkdwn
  const htmlEsc = s => String(s || '')
    .replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
  const mdEsc = s => htmlEsc(s).replace(/([_*~`])/g, '\\$1');
  const domain = u => { try { return new URL(u).hostname.replace(/^www\./,''); } catch { return ''; } };
  const parseDate = v => { const d = new Date(v || Date.now()); return isNaN(d) ? new Date() : d; };
  const toIST = v => {
    try { return parseDate(v).toLocaleString('en-IN',{ timeZone:'Asia/Kolkata', hour12:false }); }
    catch { return ''; }
  };

  const body = arr
    .sort((x,y) => new Date(y.date || y?.json?.date || 0) - new Date(x.date || x?.json?.date || 0))
    .map((it) => {
      const url = it.url ?? it?.json?.url ?? '';
      const title = mdEsc(it.title ?? it?.json?.title ?? '(untitled)');
      const src = (it.source ?? it?.json?.source) || domain(url);
      const summary = mdEsc(it.summary ?? it?.json?.summary ?? '');
      const link = url ? `<${url}|${title}>` : title;
      return `*${link}* (${src})\n${summary}`;
    })
    .join('\n\n');
  return `\n\n${body}`;
})()
}}

