// Input shape we see sometimes:
// { output: "```json\n[ {...}, {...} ]\n```" }  OR plain "[ {...} ]"
let raw = String($json.output || "").trim();

// 1) Strip code fences if present
raw = raw.replace(/^```(?:json)?\s*/i, "").replace(/```$/, "");

// 2) If there is extra chatter, extract the first JSON array
const m = raw.match(/\[[\s\S]*\]/);
const arrayText = m ? m[0] : "[]";

// 3) Parse safely (with a trailing-comma fix fallback)
let arr;
try {
  arr = JSON.parse(arrayText);
} catch {
  const fixed = arrayText.replace(/,\s*([\]}])/g, "$1");
  try { 
    arr = JSON.parse(fixed); 
  } catch { 
    arr = []; 
  }
}

// 4) Normalize minimal fields so downstream is predictable
const domainFrom = (u) => { 
  try { 
    return new URL(u).hostname.replace(/^www\./, ""); 
  } catch { 
    return ""; 
  } 
};

const items = (Array.isArray(arr) ? arr : []).map(x => ({
  title: String(x?.title || "").trim(),
  url: String(x?.url || "").trim(),
  source: domainFrom(x?.url) || String(x?.source || "").trim(),
  date: x?.date || new Date().toISOString(),
  topic: String(x?.topic || "").trim(),
  summary: String(x?.summary || "").trim(),
}));

// 5) Return clean structure
return { json: { output: { items } } };
