const items = $input.all();

const stats = {
  total_open_tickets: items.length,
  urgent_count: items.filter(i => i.json.ticket_data.priority === 'urgent').length,
  high_count: items.filter(i => i.json.ticket_data.priority === 'high').length,
  normal_count: items.filter(i => i.json.ticket_data.priority === 'normal').length,
  low_count: items.filter(i => i.json.ticket_data.priority === 'low').length,
  sla_breached: items.filter(i => i.json.sla_status === 'BREACHED').length,
  sla_at_risk: items.filter(i => i.json.sla_status === 'AT_RISK').length,
  avg_ticket_age: Math.round(items.reduce((sum, i) => sum + i.json.ticket_age_days, 0) / items.length),
  oldest_ticket_age: Math.max(...items.map(i => i.json.ticket_age_days)),
  sync_timestamp: new Date().toISOString()
};

return [{ json: stats }];
