const result = {
  summaries: [],
  company: {},
  campaign: {}
};

for (const item of items) {
  const json = item.json;

  // 1. Handle summary + hashtags
  if (json.row_number && json["summary "]) {
    const summaryText = JSON.parse(json["summary "])?.text?.trim();
    result.summaries.push({
      summary_id: `trend_summary_${json.row_number}`,
      text: summaryText || "",
      hashtags: [],
      row_number: json.row_number
    });
  }

  // 2. Add hashtags to corresponding summary
  if (json.row_number && json["hastag "]) {
    const tagList = JSON.parse(json["hastag "]);
    const match = result.summaries.find(s => s.row_number === json.row_number);
    if (match) match.hashtags = tagList;
  }

  // 3. Capture company profile
  if (json.id === "c182f59e-4819-4390-abc8-117b0a878abc") {
    result.company = {
      name: json.name,
      mission: json.mission,
      vision: json.vision,
      values: json.values,
      services: json.services,
      contact: json.contact_details,
      social_links: json.social_links,
      location: json.location,
      tone: json.tone_of_voice,
      seo_keywords: json.seo_keywords,
      testimonials: json.testimonials,
    };
  }

  // 4. Capture campaign data
  if (json.company_id === "c182f59e-4819-4390-abc8-117b0a878abc" && json.campaign_goal) {
    result.campaign = {
      goal: json.campaign_goal,
      focus: json.focus,
      success_metric: json.success_metric,
      target_audience: json.target_audience,
      customer_base: json.customer_base,
      keywords: json.keywords,
      company_name: json.name
    };
  }
}

// Optional: remove row_number after matching
for (const s of result.summaries) {
  delete s.row_number;
}

return [
  {
    json: result
  }
];
