/* Combine static pages + extra JSON, then chunk pages for model calls */
const s = $getWorkflowStaticData('global');
if (!s.pages) s.pages = [];

function normPage(p = {}) {
  return {
    url: p.url || '',
    depth: p.depth ?? null,
    content: typeof p.content === 'string' ? p.content : ''
  };
}

const incomingPageItems = items
  .filter(i => typeof i.json.content === 'string')
  .map(i => normPage(i.json));

const storedPages = (s.pages || []).map(normPage);
const pages = storedPages.length ? storedPages : incomingPageItems;

const extraJson = items
  .filter(i => typeof i.json.content !== 'string')
  .map(i => i.json);

let combinedContent = pages
  .map(p => `URL: ${p.url}\nDepth: ${p.depth}\nContent:\n${p.content}\n`)
  .join('\n-----------------\n');

if (extraJson.length) {
  combinedContent += `\n\nLINKEDIN_DATA::\n\n${JSON.stringify(extraJson)}`;
}

const CHUNK_SIZE = 5;
const MAX_CHARS_PER_BATCH = 12000;

function chunkByChars(arr, maxChars) {
  const batches = [];
  let current = [];
  let chars = 0;
  for (const it of arr) {
    const len = (it.content || '').length;
    if (current.length && chars + len > maxChars) {
      batches.push(current);
      current = [];
      chars = 0;
    }
    current.push(it);
    chars += len;
  }
  if (current.length) batches.push(current);
  return batches;
}

const charBatches = chunkByChars(pages, MAX_CHARS_PER_BATCH);
const groups = [];
for (const batch of charBatches) {
  for (let i = 0; i < batch.length; i += CHUNK_SIZE) {
    groups.push(batch.slice(i, i + CHUNK_SIZE));
  }
}

return groups.length
  ? groups.map((g, idx) => ({ json: { batchIndex: idx, pages: g, combinedContent,accId:s.accountId } }))
  : [{ json: { batchIndex: 0, pages: [], combinedContent } }];

const s = $getWorkflowStaticData('global');
s.pending = 1;
s.visited = [];
s.queued = {};
s.pages = [];

// Ensure url has a scheme so URL() won't throw
const ensureUrl = u => (/^https?:\/\//i.test(u) ? u : `https://${u}`);

try {
  $json.domain = new URL(ensureUrl($json.url)).hostname; // => "www.crmaiinsight.com"
} catch (e) {
  // Fallback if url is malformed
  $json.domain = String($json.url || '')
    .replace(/^[a-z]+:\/\//i, '')
    .replace(/\/.*$/, '')
    .replace(/:\d+$/, '');
}

return $json;

const staticData = $getWorkflowStaticData('global');
if (!Array.isArray(staticData.visited)) staticData.visited = [];
if (typeof staticData.pending !== 'number') staticData.pending = 0;
if (!staticData.queued || typeof staticData.queued !== 'object') staticData.queued = {};

const currentUrl = $('Attach URL/Depth to HTML').item.json.url.replace(/\/+$/, '');
const currentDepth = $('Attach URL/Depth to HTML').item.json.depth || 0;
const maxDepth = $('Seed Root Crawl Item').first().json.maxDepth;
const domainParamRaw = ($('Init Crawl Params').first().json.domain || '').toString();
const content = typeof $json.content === 'string' ? $json.content : '';

const PROTO_RE = /^[a-zA-Z][a-zA-Z0-9+.-]*:\/\//;

// Normalize a host string: strip protocol, path, and leading "www."
function hostOf(u) {
  if (!u) return '';
  let s = u.toString();
  if (PROTO_RE.test(s)) s = s.replace(PROTO_RE, '');
  const i = s.indexOf('/');
  if (i !== -1) s = s.slice(0, i);
  return s.toLowerCase().replace(/^www\./, '');
}

// Build absolute URL from href + base without using URL()
function toAbsolute(href, base) {
  if (!href) return '';
  const h = href.trim();
  if (PROTO_RE.test(h)) return h.replace(/\/+$/, '');
  if (h.startsWith('//')) {
    const proto = (base.match(PROTO_RE) || ['https://'])[0];
    return (proto + h.slice(2)).replace(/\/+$/, '');
  }
  if (h.startsWith('/')) {
    const baseHost = base.replace(PROTO_RE, '').split('/')[0];
    const proto = (base.match(PROTO_RE) || ['https://'])[0];
    return (proto + baseHost + h).replace(/\/+$/, '');
  }
  // relative path
  let dir = base;
  if (!dir.endsWith('/')) {
    const cut = dir.lastIndexOf('/');
    dir = cut > (dir.indexOf('://') + 2) ? dir.slice(0, cut + 1) : (dir + '/');
  }
  return (dir + h).replace(/\/+$/, '');
}

function extractHostname(abs) {
  let s = abs.replace(PROTO_RE, '');
  const i = s.indexOf('/');
  const host = (i === -1 ? s : s.slice(0, i)).toLowerCase();
  return host.replace(/^www\./, '');
}

const allowedHost = hostOf(domainParamRaw) || hostOf(currentUrl);
const currentHost  = hostOf(currentUrl);

// mark current as visited & dequeue
if (!staticData.visited.includes(currentUrl)) staticData.visited.push(currentUrl);
delete staticData.queued[currentUrl];

const links = Array.isArray($json.links) ? $json.links : [];
const newLinks = [];
const queuedLocal = new Set();

for (const link of links) {
  if (!link) continue;
  const l = String(link).trim();
  if (!l || l.startsWith('mailto:') || l.startsWith('tel:') || l.startsWith('javascript:')) continue;
  if (l.includes('#')) continue;
  if (/\.(pdf|docx?|xlsx?|pptx?)($|\?)/i.test(l)) continue;

  const absolute = toAbsolute(l, currentUrl);
  const host = extractHostname(absolute);

  // treat apex and www as same-site
  const sameSite = (host === allowedHost) || (host === currentHost);

  if (
    sameSite &&
    !staticData.visited.includes(absolute) &&
    !staticData.queued[absolute] &&
    !queuedLocal.has(absolute) &&
    currentDepth < maxDepth
  ) {
    newLinks.push({
      json: { url: absolute, depth: currentDepth + 1, type: 'link', maxDepth }
    });
    queuedLocal.add(absolute);
    staticData.queued[absolute] = true;
  }
}

staticData.pending += newLinks.length;
staticData.pending--; // this page done

return newLinks.concat({
  json: { url: currentUrl, depth: currentDepth, content, type: 'page', maxDepth }
});
