const results = [];

for (const item of $input.all()) {
  const binaryData = item.binary;
  const jsonData = item.json;

  if (!binaryData || typeof binaryData !== 'object') {
    results.push({
      json: {
        error: "No binary data found in item",
        originalJson: jsonData,
        itemIndex: results.length
      }
    });
    continue;
  }

  const attachmentMetadata = {};
  const attachmentList = [];
  let totalSize = 0;

  for (const [key, binaryItem] of Object.entries(binaryData)) {
    if (binaryItem && binaryItem.data) {
      const metadata = {
        key: key,
        mimeType: binaryItem.mimeType || 'unknown',
        fileName: binaryItem.fileName || key,
        fileExtension: binaryItem.fileName ? 
          binaryItem.fileName.split('.').pop().toLowerCase() : 'unknown',
        sizeBytes: binaryItem.data ? Buffer.byteLength(binaryItem.data) : 0,
        sizeMB: binaryItem.data ? 
          (Buffer.byteLength(binaryItem.data) / 1024 / 1024).toFixed(2) : 0,
        hasData: !!binaryItem.data,
        encoding: 'binary',
        fileType: 'unknown'
      };

      attachmentMetadata[key] = metadata;
      attachmentList.push(metadata);
      totalSize += metadata.sizeBytes;
    }
  }

  const summary = {
    totalAttachments: attachmentList.length,
    totalSizeBytes: totalSize,
    totalSizeMB: (totalSize / 1024 / 1024).toFixed(2)
  };

  results.push({
    json: {
      summary: summary,
      attachments: attachmentMetadata,
      attachmentList: attachmentList,
      originalJson: jsonData,
      extractedAt: new Date().toISOString(),
      itemIndex: results.length
    }
  });
}

return results;

function processAttachments(attachmentsArray, messageId) {
  if (!attachmentsArray || !Array.isArray(attachmentsArray)) {
    return null;
  }

  const emailAttachments = attachmentsArray.filter(item => 
    item.messageId === messageId
  );

  if (emailAttachments.length === 0) {
    return null;
  }

  const processedAttachments = emailAttachments.map(item => {
    const att = item.attachments || {};
    return {
      key: att.key || '',
      mimeType: att.mimeType || '',
      fileName: att.fileName || '',
      fileExtension: att.fileExtension || '',
      sizeBytes: att.sizeBytes || 0,
      sizeMB: att.sizeMB || '0',
      hasData: att.hasData || false,
      encoding: att.encoding || '',
      fileType: att.fileType || '',
      originalFileSize: att.originalFileSize || ''
    };
  });

  return JSON.stringify(processedAttachments);
}

const transformedItems = [];

try {
  const inputData = $input.all().map(item => item.json).flat();

  let attachmentsData = [];
  const emailsData = [];

  inputData.forEach((item, index) => {
    if (item.adjunto && Array.isArray(item.adjunto)) {
      attachmentsData = attachmentsData.concat(item.adjunto);
    } else if (item.message_id) {
      emailsData.push(item);
    }
  });

  emailsData.forEach(emailData => {
    const emailAttachments = processAttachments(attachmentsData, emailData.message_id);

    const transformedMessage = {
      message_id: emailData.message_id,
      thread_id: emailData.thread_id,
      sender: JSON.stringify(emailData.sender),
      recipients: JSON.stringify(emailData.recipients),
      labels: JSON.stringify(emailData.labels),
      subject: emailData.subject,
      body: emailData.body,
      attachments: emailAttachments,
      size: emailData.size,
      timestamp: emailData.timestamp,
      is_read: emailData.is_read,
      is_outgoing: emailData.is_outgoing,
      is_deleted: emailData.is_deleted,
      last_indexed: emailData.last_indexed
    };

    transformedItems.push({ json: transformedMessage });
  });

} catch (error) {
  transformedItems.push({
    json: {
      error: true,
      message: error.message,
      timestamp: new Date().toISOString()
    }
  });
}

return transformedItems;

// Gmail to PostgreSQL transformation
const authenticatedUserEmail = 'user@example.com';

function extractTextFromHtml(htmlContent) {
  if (!htmlContent) return '';
  return htmlContent.replace(/<[^>]+>/g, '').replace(/\s+/g, ' ').trim().substring(0, 500) || '';
}

function normalizeEmailAddress(emailObj) {
  if (!emailObj) return { email: '', name: '' };
  return {
    email: emailObj.address || emailObj.email || '',
    name: emailObj.name || ''
  };
}

const transformedItems = [];

for (const item of $input.all()) {
  const gmailData = item.json;

  const fromInfo = gmailData.from?.value?.[0] || {};
  const sender = normalizeEmailAddress(fromInfo);

  const recipients = {
    to: (gmailData.to?.value || []).map(normalizeEmailAddress),
    cc: (gmailData.cc?.value || []).map(normalizeEmailAddress),
    bcc: (gmailData.bcc?.value || []).map(normalizeEmailAddress)
  };

  const labels = gmailData.labelIds || [];

  let body = '';
  if (gmailData.text && gmailData.text.trim().length > 0) {
    body = gmailData.text.trim().substring(0, 1000);
  } else if (gmailData.textAsHtml && gmailData.textAsHtml.trim().length > 0) {
    body = gmailData.textAsHtml.replace(/<[^>]+>/g, '').trim().substring(0, 1000);
  } else if (gmailData.html) {
    body = extractTextFromHtml(gmailData.html);
  }

  if (!body || body.length === 0) {
    body = gmailData.subject || 'No content available';
  }

  const isRead = !labels.includes('UNREAD');
  const isOutgoing = sender.email.toLowerCase() === authenticatedUserEmail.toLowerCase();

  let timestamp = new Date().toISOString();
  if (gmailData.date) {
    try {
      timestamp = new Date(gmailData.date).toISOString();
    } catch (error) {
      timestamp = new Date().toISOString();
    }
  }

  const transformedMessage = {
    message_id: gmailData.id,
    thread_id: gmailData.threadId || gmailData.id,
    sender: sender,
    recipients: recipients,
    labels: labels,
    subject: gmailData.subject || '',
    body: body,
    attachments: null,
    size: gmailData.sizeEstimate || 0,
    timestamp: timestamp,
    is_read: isRead,
    is_outgoing: isOutgoing,
    is_deleted: false,
    last_indexed: new Date().toISOString()
  };

  transformedItems.push({ json: transformedMessage });
}

return transformedItems;
