const items = $input.all();
const requestNumbers = items.map((item) => {
  const match = item?.json?.chatInput.match(/REQ\d+/);
  return { requestNumber: match ? match[0] : null };
});
return requestNumbers;

const items = $input.all();
const incidentNumbers = items.map((item) => {
  const match = item?.json?.chatInput.match(/INC\d+/);
  return { incidentNumber: match ? match[0] : null };
});
return incidentNumbers;

const text = items.map(item => item.json.output.text).join('\n\n');
const cleaned = text
  .replace(/\\"/g, '"')   // Replace escaped quotes
  .replace(/\\n/g, '\n')  // Replace escaped newlines
  .replace(/\\r/g, '')    // Remove escaped carriage returns
  .replace(/\\\\/g, '\\'); // Replace double backslashes with single

return [
  {
    json: {
      finalText: cleaned
    }
  }
];

