const { pageStart = 1, pageLimit = 50 } = $json;
const out = [];
for (let p = pageStart; p <= pageLimit; p++) out.push({ json: { page: p } });
return out;

const places = $json.places || [];
return places.map(p => ({
  json: {
    UUID: p.cid || '',
    Name: p.title || '',
    Address: p.address || '',
    Website: p.website || '',
    Rating: p.rating ?? '',
    Email: '',
    Phone: String(p.phoneNumber || '').replace(/^\+/, '').replace(/\s+/g, ''),
    "Opening Hours": ''
  }
}));

const items = $input.all()[0].json.choices[0].message.content;
const cleaned = items.replace('```json', '').replace('```', '')
const parsed = JSON.parse(cleaned);

return {data: parsed}

