// n8n Code node
// Input: 1 item that contains `json.data` array
// Output: one item with a single JSON array erpLedger

const data = items[0].json.data;   // all rows live here

const ledger = data
  .map(d => {
    const row = d.values?.[0];     // ["Ansys", 1, "08-15-2025", 5096.96]
    if (!row || row.length < 4) return null;

    return {
      CustomerName: row[0],              // first column
      invoice_number: row[1],            // second column
      invoice_due_date: row[2],          // third column
      amount: Number(row[3]),            // fourth column
      id: String(row[1])                 // use invoice number as ID
    };
  })
  .filter(r => r !== null);

return [
  {
    json: {
      erpLedger: ledger
    }
  }
];

// n8n Code node
// Input: one item with .json.output (string containing transactions, matches, summary)
// Output: multiple items (one per row in the reconciliation table)

const raw = $input.first().json.output;

// ---------- Step 1: Parse safely ----------
let transactions = [];
let matches = [];
let summary = {};

try {
  // Normalize separators: replace triple dashes with blank lines
  const normalized = raw.replace(/---/g, "\n\n");

  // Split into JSON blocks
  const blocks = normalized
    .split(/\n\s*\n/)
    .map(b => b.trim())
    .filter(Boolean);

  if (blocks[0]) {
    transactions = JSON.parse(blocks[0]);
  }
  if (blocks[1]) {
    matches = JSON.parse(blocks[1]);
  }
  if (blocks[2]) {
    summary = JSON.parse(blocks[2]);
  }
} catch (e) {
  return [{
    json: { error: "Parse failed", message: e.message, rawStart: raw.substring(0, 200) }
  }];
}

// ---------- Step 2: Index matches by transaction_id ----------
const matchMap = {};
for (const m of matches) {
  matchMap[m.transaction_id] = {
    ...m,
    // Normalize classification fields
    unmatched_classification: m.unmatched_classification || m.classification || null
  };
}

// ---------- Step 3: Build reconciliation rows ----------
const rows = [];

for (const txn of transactions) {
  const m = matchMap[txn.transaction_id];

  if (m && Array.isArray(m.matches) && m.matches.length > 0) {
    // Matched transaction (can have multiple invoices)
    for (const match of m.matches) {
      rows.push({
        "Bank Transaction Date": new Date(txn.date).toLocaleDateString("en-US"),
        "Bank Transaction Description": txn.description,
        "Bank Amount": txn.amount,
        "ERP Invoice Number(s)": match.invoice_number || null,
        "ERP Customer Name(s)": "N/A",  // not provided in your JSON
        "ERP Amount(s)": txn.amount,
        "Match Status": "Matched",
        "Confidence Score": match.confidence || null,
        "Reason": match.reason || ""
      });
    }
  } else {
    // Unmatched transaction
    rows.push({
      "Bank Transaction Date": new Date(txn.date).toLocaleDateString("en-US"),
      "Bank Transaction Description": txn.description,
      "Bank Amount": txn.amount,
      "ERP Invoice Number(s)": null,
      "ERP Customer Name(s)": "N/A",
      "ERP Amount(s)": null,
      "Match Status": m?.unmatched_classification || "Unapplied",
      "Confidence Score": null,
      "Reason": m?.reason || "No match"
    });
  }
}

// ---------- Step 4: Return as n8n items ----------
return rows.map(r => ({ json: r }));
