// Function to calculate Levenshtein distance
function levenshteinDistance(str1, str2) {
  const matrix = [];

  for (let i = 0; i <= str2.length; i++) {
    matrix[i] = [i];
  }

  for (let j = 0; j <= str1.length; j++) {
    matrix[0][j] = j;
  }

  for (let i = 1; i <= str2.length; i++) {
    for (let j = 1; j <= str1.length; j++) {
      if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(
          matrix[i - 1][j - 1] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j] + 1
        );
      }
    }
  }

  return matrix[str2.length][str1.length];
}

// Function to calculate similarity percentage
function calculateSimilarity(str1, str2) {
  if (!str1 || !str2) return 0;

  const maxLength = Math.max(str1.length, str2.length);
  if (maxLength === 0) return 100;

  const distance = levenshteinDistance(str1.toLowerCase(), str2.toLowerCase());
  return Math.round(((maxLength - distance) / maxLength) * 100);
}

// Extract customers array from items - each item contains one customer
const customers = items.map(item => item.json);
const suggestions = [];
const processedCustomers = new Set();

console.log(`Processing ${customers.length} customers for duplicates`);

// Group customers by email and name for efficient duplicate detection
const emailGroups = new Map();
const nameGroups = new Map();

customers.forEach(customer => {
  // Group by email
  if (customer.email) {
    const emailKey = customer.email.toLowerCase();
    if (!emailGroups.has(emailKey)) {
      emailGroups.set(emailKey, []);
    }
    emailGroups.get(emailKey).push(customer);
  }

  // Group by name (for name similarity matching)
  if (customer.name) {
    const nameKey = customer.name.toLowerCase();
    if (!nameGroups.has(nameKey)) {
      nameGroups.set(nameKey, []);
    }
    nameGroups.get(nameKey).push(customer);
  }
});

// Process email-based duplicates
emailGroups.forEach((group, email) => {
  if (group.length > 1) {
    // Sort by creation date to make the oldest one primary
    group.sort((a, b) => a.created - b.created);
    const primary = group[0];

    // Create suggestions for all others against the primary
    for (let i = 1; i < group.length; i++) {
      const secondary = group[i];
      if (!processedCustomers.has(secondary.id)) {
        let confidenceScore = 95;
        let matchReason = 'Email match';

        // Higher confidence if names also match
        if (primary.name && secondary.name && primary.name.toLowerCase() === secondary.name.toLowerCase()) {
          confidenceScore = 99;
          matchReason = 'Email + Name exact match';
        }

        suggestions.push({
          primary_customer_id: primary.id,
          secondary_customer_id: secondary.id,
          email: primary.email || secondary.email || '',
          name_similarity_score: confidenceScore,
          primary_name: primary.name || '',
          secondary_name: secondary.name || '',
          match_reason: matchReason,
          status: 'Pending Review'
        });

        processedCustomers.add(secondary.id);
      }
    }
    processedCustomers.add(primary.id);
  }
});

// Process name-based duplicates (only for customers not already processed by email)
