      // Sort by creation date to make the oldest one primary
      unprocessedGroup.sort((a, b) => a.created - b.created);
      const primary = unprocessedGroup[0];

      // Create suggestions for all others against the primary
      for (let i = 1; i < unprocessedGroup.length; i++) {
        const secondary = unprocessedGroup[i];
        const nameSimilarity = calculateSimilarity(primary.name, secondary.name);

        if (nameSimilarity >= 80) {
