// Get the current input (Stripe customer)
const stripeCustomer = $input.item.json;

// Try to get Shopify order from various sources
let shopifyOrder = null;

// Method 1: Try from execution data
try {
  const executionData = $workflow.data;
  console.log('Execution Data:', JSON.stringify(executionData, null, 2));
} catch (e) {
  console.log('Cannot access execution data');
}

// Method 2: Try from items
try {
  const items = $items();
  console.log('Items:', JSON.stringify(items, null, 2));

  // Look for Shopify order in items
  for (const item of items) {
    if (item.line_items) {
      shopifyOrder = item;
      break;
    }
  }
} catch (e) {
  console.log('Cannot access items');
}

// Method 3: Create a test line item if no Shopify data found
if (!shopifyOrder || !shopifyOrder.line_items || shopifyOrder.line_items.length === 0) {
  console.log('No Shopify order found, creating test data');

  // Return test data to verify the node works
  return [
    {
      stripe_customer: stripeCustomer,
      line_item: {
        id: 'test_item_1',
        title: 'Test Product',
        price: '99.99',
        quantity: 1
      },
      price: 99.99,
      quantity: 1,
      title: 'Test Product',
      customer_id: stripeCustomer.id,
      currency: 'USD'
    }
  ];
}

// If we have Shopify order, process it
const lineItems = shopifyOrder.line_items || [];
console.log('Processing line items:', lineItems);

// Return processed line items
return lineItems.map((item, index) => ({
  shopify_order: shopifyOrder,
  stripe_customer: stripeCustomer,
  line_item: item,
  price: parseFloat(item.price || '0'),
  quantity: parseInt(item.quantity || '1'),
  title: item.title || `Item ${index + 1}`,
  customer_id: stripeCustomer.id,
  currency: shopifyOrder.currency || 'USD'
}));

// Get the invoice data from the HTTP response
const invoice = $input.item.json;

// Format the data for Google Sheets
const formattedData = {
  // Invoice Information
  stripe_invoice_id: invoice.id,
  invoice_number: invoice.number,
  invoice_status: invoice.status,

  // Customer Information
  customer_id: invoice.customer,
  customer_name: invoice.customer_name,
  customer_email: invoice.customer_email,
  customer_phone: invoice.customer_phone,

  // Financial Information
  currency: invoice.currency.toUpperCase(),
  amount_due: (invoice.amount_due / 100).toFixed(2), // Convert from cents to dollars
  amount_paid: (invoice.amount_paid / 100).toFixed(2),
  amount_remaining: (invoice.amount_remaining / 100).toFixed(2),
  subtotal: (invoice.subtotal / 100).toFixed(2),
  total: (invoice.total / 100).toFixed(2),

  // Invoice URLs
  hosted_invoice_url: invoice.hosted_invoice_url,
  invoice_pdf: invoice.invoice_pdf,

  // Dates (converted to readable format)
  created_date: new Date(invoice.created * 1000).toISOString(),
  due_date: invoice.due_date ? new Date(invoice.due_date * 1000).toISOString() : null,
  finalized_at: invoice.status_transitions.finalized_at ? new Date(invoice.status_transitions.finalized_at * 1000).toISOString() : null,
  paid_at: invoice.status_transitions.paid_at ? new Date(invoice.status_transitions.paid_at * 1000).toISOString() : null,

  // Additional Information
  collection_method: invoice.collection_method,
  billing_reason: invoice.billing_reason,
  line_items_count: invoice.lines.total_count,

  // Timestamp for when this record was processed
  processed_timestamp: new Date().toISOString(),

  // Original Shopify Order Reference (if available from previous nodes)
  shopify_order_id: null // This will be populated if you have Shopify data in workflow
};

// Try to get Shopify order ID from previous nodes if available
try {
  const shopifyData = $('Process Line Items').item.json.shopify_order;
  if (shopifyData && shopifyData.id) {
    formattedData.shopify_order_id = shopifyData.id;
  }
} catch (e) {
  console.log('No Shopify order data found');
}

// Log the formatted data for debugging
console.log('Formatted data for Google Sheets:', formattedData);

return formattedData;
