// Input is the whole response with data[]
const disputes = $json.data || [];
if (disputes.length === 0) {
  return [];
}

// Stripe returns newest first, so just take index 0
const d = disputes[0];

return [{
  dispute_id: d.id,
  charge_id: d.charge,
  payment_intent: d.payment_intent,
  amount: (d.amount / 100).toFixed(2),
  currency: d.currency.toUpperCase(),
  reason: d.reason,
  status: d.status,
  created_at: new Date(d.created * 1000).toISOString(),
  respond_by: new Date(d.evidence_details.due_by * 1000).toISOString(),
  customer_email: d.evidence.customer_email_address || "",
  customer_name: d.evidence.customer_name || "",
  dispute_fee: d.balance_transactions?.[0]?.fee || 0,
  fee_currency: d.balance_transactions?.[0]?.currency || d.currency
}];

