// Debug: Log the incoming data first
console.log('Incoming data:', JSON.stringify($input.all(), null, 2));

// Get the data from webhook
const inputData = $input.first();
console.log('Input item:', JSON.stringify(inputData, null, 2));

// Extract JSON data (webhook data is nested differently)
let data;
if (inputData.json.body) {
  // If data comes in request body
  data = inputData.json.body;
} else if (inputData.json.query) {
  // If data comes as query parameters
  data = inputData.json.query;
} else {
  // Direct JSON data
  data = inputData.json;
}

console.log('Processed data:', JSON.stringify(data, null, 2));

// Handle case where data might be a string (needs parsing)
if (typeof data === 'string') {
  try {
    data = JSON.parse(data);
  } catch (e) {
    throw new Error('Invalid JSON format in request');
  }
}

// Required fields validation
const required = ['name', 'email', 'social_handles', 'niche', 'country'];
const missing = required.filter(field => !data || !data[field]);

if (missing.length > 0) {
  console.log('Available fields:', Object.keys(data || {}));
  throw new Error(`Missing required fields: ${missing.join(', ')}. Available fields: ${Object.keys(data || {}).join(', ')}`);
}

// Email format validation
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(data.email)) {
  throw new Error('Invalid email format');
}

// Clean social handles (remove @ symbols, extract usernames)
const cleanHandles = {};
for (const [platform, handle] of Object.entries(data.social_handles)) {
  if (handle) {
    cleanHandles[platform] = handle.replace(/[@\/]/g, '').split('/').pop();
  }
}

return [{
  json: {
    ...data,
    social_handles: cleanHandles,
    created_at: new Date().toISOString(),
    status: 'pending_validation'
  }
}];

const instagramResponse = $input.first().json;

// Get the user data from a previous node (you need to reference the correct node name)
let userData;
try {
  // Replace 'Data Sanitizer' with your actual previous node name that has user data
  userData = $('Data Sanitizer').first().json;
} catch (e) {
  // Fallback if node reference fails
  userData = {
    name: 'Test User',
    email: 'test@example.com',
    niche: 'sports',
    country: 'US',
    rate_card: 1000,
    social_handles: { instagram: 'cristiano' }
  };
}

console.log('User Data:', JSON.stringify(userData, null, 2));
console.log('Instagram Response:', JSON.stringify(instagramResponse, null, 2));

try {
  const profileData = instagramResponse.result;

  if (!profileData) {
    throw new Error('No Instagram profile data found');
  }

  // Parse the real Instagram data from RapidAPI
  const instagramData = {
    id: profileData.id,
    username: profileData.username,
    followers: profileData.edge_followed_by.count || 0,
    following: profileData.edge_follow.count || 0,
    posts_count: profileData.edge_owner_to_timeline_media.count || 0,
    is_private: profileData.is_private || false,
    verified: profileData.is_verified || false,
    biography: profileData.biography || '',
    full_name: profileData.full_name || '',
    profile_pic_url: profileData.profile_pic_url || '',
    profile_pic_url_hd: profileData.profile_pic_url_hd || ''
  };

  // Calculate realistic engagement for mega-influencer (Cristiano)
  let engagement_rate = 1.2; // Mega influencers typically have lower engagement rates
  if (instagramData.followers < 1000000) {
    engagement_rate = 3.5; // Regular influencers
  } else if (instagramData.followers < 100000000) {
    engagement_rate = 1.8; // Major influencers  
  }

  const avg_engagement = Math.floor(instagramData.followers * (engagement_rate / 100));

  instagramData.engagement_rate = parseFloat(engagement_rate.toFixed(2));
  instagramData.avg_engagement = avg_engagement;
  instagramData.avg_likes = Math.floor(avg_engagement * 0.85);
  instagramData.avg_comments = Math.floor(avg_engagement * 0.15);

  return [{
    json: {
      ...userData,
      social_stats: {
        instagram: {
          ...instagramData,
          api_source: 'rapidapi_real',
          fetched_at: new Date().toISOString()
        }
      }
    }
  }];

} catch (error) {
  console.log('Parse error:', error.message);

  return [{
    json: {
      ...userData,
      social_stats: {
        instagram: {
          followers: 0,
          following: 0,
          posts_count: 0,
          verified: false,
          biography: '',
          username: userData.social_handles?.instagram || 'unknown',
          avg_engagement: 0,
          engagement_rate: 0,
          api_source: 'parse_failed',
          error: error.message,
          fetched_at: new Date().toISOString()
        }
      }
    }
  }];
}

const input = $input.first().json;

console.log('Full YouTube input:', JSON.stringify(input, null, 2));

// Try to get user data from previous nodes in the workflow
let userData;
try {
  // Try different possible node names - update these to match your actual node names
  userData = $('Parse Instagram Data').first().json || 
             $('Data Sanitizer').first().json ||
             $('Process Email Validation').first().json ||
             $('Mock Instagram Data').first().json;

  console.log('Found user data from previous node:', JSON.stringify(userData, null, 2));
} catch (e) {
  console.log('Could not find user data from previous nodes, using fallback');
  // Fallback user data for testing
  userData = {
    name: "Sarah Johnson",
    email: "user@example.com",
    niche: "fitness", 
    country: "US",
    rate_card: 750,
    social_handles: {
      instagram: "sarahfitlife",
      youtube: "SarahFitnessJourney"
    },
    social_stats: {},
    created_at: new Date().toISOString(),
    status: 'processing'
  };
}

// Parse the YouTube channel data from the current input
const channelData = input;

try {
  if (!channelData.stats) {
    throw new Error('No YouTube stats found in response');
  }

  const youtubeData = {
    channel_id: channelData.channelId || '',
    username: channelData.username || '',
    title: channelData.title || '',
    subscribers: channelData.stats.subscribers || 0,
    subscribers_text: channelData.stats.subscribersText || '',
    total_views: channelData.stats.views || 0,
    video_count: channelData.stats.videos || 0,
    description: channelData.description || '',
    is_verified: channelData.isVerified || false,
    is_verified_artist: channelData.isVerifiedArtist || false,
    joined_date: channelData.joinedDate || '',
    joined_date_text: channelData.joinedDateText || '',
    country: channelData.country || '',
    has_business_email: channelData.hasBusinessEmail || false,
    avatar_url: channelData.avatar?.[0]?.url || '',
    banner_url: channelData.banner?.desktop?.[0]?.url || '',
    is_family_safe: channelData.isFamilySafe || true
  };

  // Calculate engagement metrics
  const avg_views_per_video = youtubeData.video_count > 0 ? 
    Math.floor(youtubeData.total_views / youtubeData.video_count) : 0;

  // YouTube engagement rate: (avg views per video / subscribers) * 100
  const engagement_rate = youtubeData.subscribers > 0 ? 
    parseFloat(((avg_views_per_video / youtubeData.subscribers) * 100).toFixed(2)) : 0;

  // Estimate social engagement based on YouTube norms
  const estimated_avg_likes = Math.floor(avg_views_per_video * 0.03); // ~3% of views
  const estimated_avg_comments = Math.floor(avg_views_per_video * 0.005); // ~0.5% of views
  const estimated_total_engagement = estimated_avg_likes + estimated_avg_comments;

  // Add calculated metrics
  youtubeData.avg_views_per_video = avg_views_per_video;
  youtubeData.engagement_rate = engagement_rate;
  youtubeData.estimated_avg_likes = estimated_avg_likes;
  youtubeData.estimated_avg_comments = estimated_avg_comments;
  youtubeData.estimated_total_engagement = estimated_total_engagement;

  // Channel age calculation (for scoring purposes)
  const joinedDate = new Date(channelData.joinedDate);
  const now = new Date();
  const monthsActive = Math.floor((now - joinedDate) / (1000 * 60 * 60 * 24 * 30));
  youtubeData.months_active = monthsActive;
  youtubeData.videos_per_month = monthsActive > 0 ? (youtubeData.video_count / monthsActive).toFixed(1) : 0;

  console.log('Successfully parsed YouTube data:', JSON.stringify(youtubeData, null, 2));

  return [{
    json: {
      ...userData,
      social_stats: {
        ...userData.social_stats,
        youtube: {
          ...youtubeData,
          api_source: 'rapidapi_real',
          fetched_at: new Date().toISOString()
        }
      }
    }
  }];

} catch (error) {
  console.log('YouTube Parse Error:', error.message);

  return [{
    json: {
      ...userData,
      social_stats: {
        ...userData.social_stats,
        youtube: {
          channel_id: '',
          username: '',
          title: '',
          subscribers: 0,
          total_views: 0,
          video_count: 0,
          engagement_rate: 0,
          avg_views_per_video: 0,
          estimated_avg_likes: 0,
          estimated_avg_comments: 0,
          api_source: 'parse_failed',
          error: error.message,
          fetched_at: new Date().toISOString()
        }
      }
    }
  }];
}

const allData = $input.all(); // Get all items from merge node
console.log('Number of merge items:', allData.length);

// Find the item with both Instagram and YouTube data
let data;
if (allData.length === 2) {
  // Two separate items from merge - combine them
  const item1 = allData[0].json;
  const item2 = allData[1].json;

  data = {
    ...item1,
    social_stats: {
      ...item1.social_stats,
      ...item2.social_stats
    }
  };
} else {
  // Single item with both platforms
  data = allData[0].json;
}

const socialStats = data.social_stats || {};

console.log('Combined data check:', {
  hasInstagram: !!socialStats.instagram,
  hasYoutube: !!socialStats.youtube,
  instagramFollowers: socialStats.instagram?.followers,
  youtubeSubscribers: socialStats.youtube?.subscribers
});

// Rest of scoring configuration (same as before)
const SCORING_CONFIG = {
  platform_weights: {
    instagram: 0.6,
    youtube: 0.4
  },
  follower_tiers: {
    nano: { min: 1000, max: 10000, score: 50 },
    micro: { min: 10000, max: 100000, score: 70 },
    macro: { min: 100000, max: 1000000, score: 90 },
    mega: { min: 1000000, max: Infinity, score: 100 }
  },
  engagement_tiers: {
    youtube: {
      excellent: { min: 10, score: 100 },
      good: { min: 5, score: 85 },
      average: { min: 2, score: 65 },
      poor: { min: 0, score: 40 }
    },
    instagram: {
      excellent: { min: 6, score: 100 },
      good: { min: 3, score: 85 },
      average: { min: 1.5, score: 65 },
      poor: { min: 0, score: 40 }
    }
  }
};

function calculatePlatformScore(platformData, platform) {
  if (!platformData || platformData.error) return null;

  const followers = platformData.followers || platformData.subscribers || 0;
  const engagementRate = platformData.engagement_rate || 0;

  let followerScore = 40;
  for (const [tier, config] of Object.entries(SCORING_CONFIG.follower_tiers)) {
    if (followers >= config.min && followers <= config.max) {
      followerScore = config.score;
      break;
    }
  }

  let engagementScore = 40;
  const thresholds = SCORING_CONFIG.engagement_tiers[platform] || SCORING_CONFIG.engagement_tiers.instagram;

  for (const [tier, config] of Object.entries(thresholds)) {
    if (engagementRate >= config.min) {
      engagementScore = config.score;
      break;
    }
  }

  let qualityBonus = 0;
  if (platformData.verified || platformData.is_verified) qualityBonus += 10;
  if (platformData.has_business_email) qualityBonus += 5;
  if (platform === 'youtube' && parseFloat(platformData.videos_per_month) > 4) qualityBonus += 5;
  if (platform === 'instagram' && !platformData.is_private) qualityBonus += 5;

  const totalScore = Math.min(100, Math.round(
    (followerScore * 0.4) + 
    (engagementScore * 0.5) + 
    qualityBonus
  ));

  return {
    total: totalScore,
    breakdown: {
      follower_score: followerScore,
      engagement_score: engagementScore,
      quality_bonus: qualityBonus,
      followers: followers,
      engagement_rate: engagementRate,
      tier: followers >= 1000000 ? 'mega' : followers >= 100000 ? 'macro' : followers >= 10000 ? 'micro' : 'nano'
    }
  };
}

// Calculate scores for each platform
const scores = {};
let weightedScore = 0;
let totalWeight = 0;

// Instagram scoring
if (socialStats.instagram && !socialStats.instagram.error) {
  scores.instagram = calculatePlatformScore(socialStats.instagram, 'instagram');
  if (scores.instagram) {
    weightedScore += scores.instagram.total * SCORING_CONFIG.platform_weights.instagram;
    totalWeight += SCORING_CONFIG.platform_weights.instagram;
  }
}

// YouTube scoring  
if (socialStats.youtube && !socialStats.youtube.error) {
  scores.youtube = calculatePlatformScore(socialStats.youtube, 'youtube');
  if (scores.youtube) {
    weightedScore += scores.youtube.total * SCORING_CONFIG.platform_weights.youtube;
    totalWeight += SCORING_CONFIG.platform_weights.youtube;
  }
}

const overallScore = totalWeight > 0 ? Math.round(weightedScore / totalWeight) : 0;

// Status determination
const totalFollowers = (socialStats.instagram?.followers || 0) + (socialStats.youtube?.subscribers || 0);
const followersPerDollar = totalFollowers / (data.rate_card || 1);

let status = 'rejected_low_score';
let rejection_reason = '';

if (totalFollowers < 5000) {
  status = 'rejected_insufficient_reach';
  rejection_reason = 'Total audience below 5K minimum';
} else if (data.rate_card > 2000 && followersPerDollar < 100) {
  status = 'rejected_overpriced';
  rejection_reason = 'Rate card too high for audience size';
} else if (overallScore >= 80) {
  status = 'approved';
} else if (overallScore >= 65) {
  status = 'pending_review';
} else {
  status = 'rejected_low_score';
  rejection_reason = `Score ${overallScore}/100 below threshold`;
}

return [{
  json: {
    ...data,
    scoring: {
      platform_scores: scores,
      overall_score: overallScore,
      total_followers: totalFollowers,
      followers_per_dollar: Math.round(followersPerDollar),
      calculated_at: new Date().toISOString()
    },
    status: status,
    rejection_reason: rejection_reason || null
  }
}];
