const items = $input.all();
const updatedItems = items.map((item) => {
  let rawNumber = item?.json["number"];
  rawNumber = rawNumber ? String(rawNumber) : "";
  const cleanedNumber = rawNumber.replace(/\D/g, "");
  item.json["number"] = cleanedNumber;
  return item;
});
return updatedItems;

const checkouts = items[0].json.checkouts;

if (!Array.isArray(checkouts)) {
  throw new Error("Checkouts not found or not an array");
}

return checkouts.map((checkout) => {
  const customer = checkout.customer || {};
  const product = (checkout.line_items && checkout.line_items[0]) || {};

  return {
    json: {
      checkoutId: checkout.id || null,
      customerName: `${customer.first_name || ''} ${customer.last_name || ''}`.trim(),
      number: checkout.phone || customer.phone || 'N/A',
      productTitle: product.title || 'N/A',
      quantity: product.quantity || 'N/A',
      totalPrice: checkout.total_price || '0.00',
      presentment_currency: checkout.presentment_currency,
      abandonedCheckoutURL: checkout.abandoned_checkout_url || 'N/A'
    }
  };
});
