// Extract URL from Telegram message
const message = $json.message.text;
const urlRegex = /(https?:\/\/[^\s]+)/g;
const urls = message.match(urlRegex);

if (!urls || urls.length === 0) {
  throw new Error('No URL found in the message');
}

const extractedUrl = urls[0];
const chatId = $json.message.chat.id;
const messageId = $json.message.message_id;
const timestamp = new Date().toISOString();

return {
  json: {
    url: extractedUrl,
    chatId: chatId,
    messageId: messageId,
    timestamp: timestamp,
    originalMessage: message
  }
};

// Extract text content from HTML
const html = $json.data;
const url = $node['Extract URL'].json.url;

// Simple HTML to text conversion
let textContent = html
  .replace(/<script[^>]*>.*?<\/script>/gi, '')
  .replace(/<style[^>]*>.*?<\/style>/gi, '')
  .replace(/<[^>]*>/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

// Extract title (simple approach)
const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
const title = titleMatch ? titleMatch[1].trim() : 'Article';

// Limit content length for AI processing
if (textContent.length > 8000) {
  textContent = textContent.substring(0, 8000) + '...';
}

if (textContent.length < 100) {
  throw new Error('Could not extract meaningful content from the URL');
}

return {
  json: {
    url: url,
    title: title,
    content: textContent,
    wordCount: textContent.split(' ').length
  }
};

