const id = $json.post_id || $json.id || '';
const parts = id.split('_');
const postId = parts.length > 1 ? parts[1] : '';
const facebookUsername = '<YOUR_FACEBOOK_PAGE_USERNAME>';
const facebookPostUrl = postId
  ? `https://facebook.com/${facebookUsername}/posts/${postId}`
  : '';

return {
  json: {
    facebook_post_id: postId,
    facebook_post_url: facebookPostUrl,
    facebook_published: !!postId
  }
};

const mediaId = $json.id || '';
return {
  json: {
    instagram_post_id: mediaId,
    instagram_post_url: $input.first().json.permalink, // nie mamy shortcode :(
    instagram_published: !!mediaId
  }
};

const urn = $json.urn || ''; // example: urn:li:share:7342310895114420224
const postId = urn.split(':').pop(); // get the numeric ID

const linkedinPostUrl = postId 
  ? `https://www.linkedin.com/feed/update/${urn.replace('urn:li:', '').replace(/:/g, '%3A')}/`
  : '';

return {
  json: {
    linkedin_post_id: postId,
    linkedin_post_url: linkedinPostUrl,
    linkedin_published: !!postId
  }
};

