let vars;
try {
  vars = $('Loop Vars').first().json;
} catch (e) {
  vars = $('Variables').first().json;
}

const next = Number(vars?.current_page ?? 1) + 1;
vars.current_page = next;

return [
  {
    json: { ...vars, current_page: next }
  }
];

