// Extract contact IDs from deals data
const dealsData = $input.all();
const contactIds = [];

dealsData.forEach(item => {
  const deal = item.json;
  if (deal.associations && deal.associations.contacts && deal.associations.contacts.results) {
    deal.associations.contacts.results.forEach(contact => {
      contactIds.push({
        contactId: contact.id,
        dealId: deal.id,
        dealName: deal.properties.dealname
      });
    });
  }
});

return contactIds;

